import sharp from 'sharp';
import fs from 'fs';
import path from 'path';
import { fileURLToPath } from 'url';

const __filename = fileURLToPath(import.meta.url);
const __dirname = path.dirname(__filename);

const imagesDir = path.join(process.cwd(), 'public', 'images');

async function optimizeHeroImages() {
    const heroFiles = ['hero_foundry.png', 'hero_logistics.png', 'hero_scrap_yard.png'];

    for (const file of heroFiles) {
        const inputPath = path.join(imagesDir, file);

        if (!fs.existsSync(inputPath)) {
            console.log(`‚ö† Skipping ${file} - not found`);
            continue;
        }

        const outputPathWebP = path.join(imagesDir, file.replace('.png', '.webp'));

        try {
            const inputStats = fs.statSync(inputPath);

            // Convert to WebP
            await sharp(inputPath)
                .webp({ quality: 85 })
                .toFile(outputPathWebP);

            const outputStats = fs.statSync(outputPathWebP);
            const reduction = ((1 - outputStats.size / inputStats.size) * 100).toFixed(1);

            console.log(`‚úì ${file} ‚Üí ${file.replace('.png', '.webp')}`);
            console.log(`  Size: ${(inputStats.size / 1024).toFixed(1)}KB ‚Üí ${(outputStats.size / 1024).toFixed(1)}KB (${reduction}% reduction)`);
        } catch (error) {
            console.error(`‚úó Failed to convert ${file}:`, error.message);
        }
    }

    console.log('\n‚úì All hero images optimized!');
}

optimizeHeroImages().catch(console.error);

import sharp from 'sharp';
import fs from 'fs';
import path from 'path';
