'use server'

import { createClient } from '@/lib/supabase/server'
import { revalidatePath } from 'next/cache'
import { z } from 'zod'

const bookingSchema = z.object({
    service_id: z.string().uuid(),
    customer_name: z.string().min(2, "Name is required"),
    email: z.string().email("Invalid email"),
    phone: z.string().min(10, "Valid phone required"),
    address: z.string().min(5, "Address required for on-site service"),
    postal_code: z.string().min(3, "Postal code required"),
    booking_date: z.string(), // YYYY-MM-DD
    slot: z.enum(['Morning', 'Afternoon']),
    notes: z.string().optional()
})

export type CreateBookingState = {
    success: boolean
    message: string
    errors?: Record<string, string[]>
}

export async function createBooking(prevState: CreateBookingState, formData: FormData): Promise<CreateBookingState> {
    const supabase = await createClient()

    // Extract raw data
    const rawData = {
        service_id: formData.get('service_id'),
        customer_name: formData.get('customer_name'),
        email: formData.get('email'),
        phone: formData.get('phone'),
        address: formData.get('address'),
        postal_code: formData.get('postal_code'),
        booking_date: formData.get('booking_date'),
        slot: formData.get('slot'),
        notes: formData.get('notes')
    }

    // Validate
    const validated = bookingSchema.safeParse(rawData)

    if (!validated.success) {
        return {
            success: false,
            message: 'Validation failed',
            errors: validated.error.flatten().fieldErrors
        }
    }

    const { data, error } = await supabase
        .from('bookings')
        .insert(validated.data)
        .select()

    if (error) {
        console.error('Booking creation error:', error)
        return {
            success: false,
            message: 'Failed to create booking. Please try again.'
        }
    }

    revalidatePath('/tech')
    return {
        success: true,
        message: 'Booking confirmed! We will contact you shortly.'
    }
}

'use server'

import { createClient } from '@/lib/supabase/server'
import { revalidatePath } from 'next/cache'

export interface ServiceItem {
    id: string
    name: string
    category: 'Software' | 'Hardware' | 'Consulting'
    is_remote: boolean
    description: string | null
    price_range: string | null
    active: boolean
}

export type AvailabilityResponse = {
    available: boolean
    message: string
}

const HALIFAX_PREFIXES = ['B3H', 'B3J', 'B3K', 'B3L', 'B3M', 'B3N', 'B3P', 'B3R', 'B3S']

export async function getServices(): Promise<ServiceItem[]> {
    const supabase = await createClient()

    const { data, error } = await supabase
        .from('service_catalog')
        .select('*')
        .eq('active', true)
        .order('category', { ascending: true })

    if (error) {
        console.error('Error fetching services:', error)
        return []
    }

    return data as unknown as ServiceItem[]
}

export async function checkServiceAvailability(
    postalCode: string,
    serviceType: 'Software' | 'Hardware' | 'Consulting'
): Promise<AvailabilityResponse> {
    // 1. Software/Consulting is always available (Remote)
    if (serviceType !== 'Hardware') {
        return { available: true, message: 'Service available remotely.' }
    }

    // 2. Hardware requires Halifax check
    const normalizedCode = postalCode.replace(/\s/g, '').toUpperCase().substring(0, 3)

    if (HALIFAX_PREFIXES.includes(normalizedCode)) {
        return { available: true, message: 'Service available in your area.' }
    }

    return {
        available: false,
        message: 'Hardware service is currently limited to the Halifax area (B3H, B3J, B3M...). Would you like a remote software consultation instead?'
    }
}

'use server'

import { createClient } from '@/lib/supabase/server'
import { revalidatePath } from 'next/cache'

export interface InquiryData {
    fullName: string
    email: string
    phone?: string
    companyName?: string
    inquiryType: 'Quote' | 'General' | 'Partnership'
    message: string
    metadata?: any
}

export async function submitInquiry(data: InquiryData) {
    const supabase = await createClient()

    try {
        const { error } = await supabase.from('inquiries' as any).insert({
            full_name: data.fullName,
            email: data.email,
            phone: data.phone,
            company_name: data.companyName,
            inquiry_type: data.inquiryType,
            message: data.message,
            metadata: data.metadata || {},
            status: 'New'
        })

        if (error) throw error

        return { success: true }
    } catch (error: any) {
        console.error('Submission error:', error)
        return { success: false, error: error.message }
    }
}

export async function getInquiries() {
    const supabase = await createClient()

    try {
        // Admin check is handled by RLS, but double check here for cleaner error
        const { data: { user } } = await supabase.auth.getUser()
        if (!user) throw new Error('Unauthorized')

        const { data, error } = await supabase
            .from('inquiries' as any)
            .select('*')
            .order('created_at', { ascending: false }) as any

        if (error) throw error

        return { success: true, data }
    } catch (error: any) {
        console.error('Fetch error:', error)
        return { success: false, error: error.message }
    }
}

export async function updateInquiryStatus(id: string, status: string) {
    const supabase = await createClient()
    try {
        const { error } = await supabase
            .from('inquiries')
            .update({ status })
            .eq('id', id)

        if (error) throw error

        revalidatePath('/admin/inquiries')
        return { success: true }
    } catch (error: any) {
        return { success: false, error: error.message }
    }
}

'use server'

import { createClient } from '@/lib/supabase/server'

export interface MetalRate {
    symbol: string
    name: string
    price: number
    currency: string
    unit: string
    trend?: number // simulated trend
}

export interface CurrencyRate {
    code: string
    rate: number
}

export async function getMetalRates(): Promise<MetalRate[]> {
    try {
        const supabase = await createClient()
        const { data, error } = await supabase
            .from('market_rates')
            .select('*')
            .order('name')

        if (error) {
            console.error('Error fetching metal rates:', error)
            return []
        }

        // Simulate small live variance for the frontend effect
        // In a real app, this variance would come from the DB history
        return data.map((rate: any) => ({
            ...rate,
            trend: (Math.random() * 2 - 1) * 0.5 // Random trend between -0.5% and +0.5%
        }))

    } catch (err) {
        console.error('Server Action Error:', err)
        return []
    }
}

export async function getCurrencyRates(baseCurrency: string = 'USD'): Promise<CurrencyRate[]> {
    try {
        // Use open.er-api.com (Free, no key)
        const response = await fetch(`https://open.er-api.com/v6/latest/${baseCurrency}`, {
            next: { revalidate: 3600 } // Cache for 1 hour
        })

        if (!response.ok) throw new Error('Failed to fetch rates')

        const data = await response.json()

        // Filter for major currencies
        const targets = ['INR', 'AED', 'EUR', 'GBP', 'CNY', 'CAD']

        return targets.map(code => ({
            code,
            rate: data.rates[code]
        }))

    } catch (err) {
        console.error('Currency API Error:', err)
        // Fallback rates if API fails
        return [
            { code: 'INR', rate: 84.50 },
            { code: 'AED', rate: 3.67 },
            { code: 'EUR', rate: 0.92 }
        ]
    }
}

