import { useEffect, useState } from 'react'
import { AlertTriangle, Clock, FileText, TrendingUp } from 'lucide-react'
import { Card } from '@/components/ui/card'
import { Badge } from '@/components/ui/badge'
import { Button } from '@/components/ui/button'
import { Skeleton } from '@/components/ui/skeleton'
import { createClient } from '@/lib/supabase/client'
import Link from 'next/link'

interface ActionItem {
    item_type: string
    item_id: string
    deal_ref: string
    title: string
    description: string
    priority: 'Low' | 'Medium' | 'High' | 'Urgent'
    due_date: string | null
    days_remaining: number | null
}

export function ActionCenter() {
    const [items, setItems] = useState<ActionItem[]>([])
    const [isLoading, setIsLoading] = useState(true)

    useEffect(() => {
        loadActionItems()
    }, [])

    async function loadActionItems() {
        try {
            const supabase = createClient()

            const { data, error } = await supabase
                .rpc('get_action_center_items') as any

            if (error) throw error

            setItems(data || [])
        } catch (error) {
            console.error('Failed to load action items:', error)
        } finally {
            setIsLoading(false)
        }
    }

    const getPriorityBadge = (priority: string) => {
        const variants = {
            Urgent: 'destructive',
            High: 'destructive',
            Medium: 'secondary',
            Low: 'outline'
        } as const

        return variants[priority as keyof typeof variants] || 'outline'
    }

    const getPriorityIcon = (priority: string) => {
        if (priority === 'Urgent' || priority === 'High') {
            return <AlertTriangle className="h-4 w-4" />
        }
        return <Clock className="h-4 w-4" />
    }

    if (isLoading) {
        return (
            <Card className="p-6">
                <Skeleton className="h-8 w-48 mb-4" />
                <div className="space-y-3">
                    <Skeleton className="h-16 w-full" />
                    <Skeleton className="h-16 w-full" />
                    <Skeleton className="h-16 w-full" />
                </div>
            </Card>
        )
    }

    return (
        <Card className="p-6">
            <div className="flex items-center justify-between mb-6">
                <h3 className="text-lg font-semibold flex items-center gap-2">
                    <AlertTriangle className="h-5 w-5 text-amber-600" />
                    Action Center
                </h3>
                {items.length > 0 && (
                    <Badge variant="destructive">{items.length} items</Badge>
                )}
            </div>

            {items.length === 0 ? (
                <div className="text-center py-12 text-muted-foreground">
                    <TrendingUp className="h-12 w-12 mx-auto mb-4 opacity-50" />
                    <p className="font-medium">All caught up!</p>
                    <p className="text-sm">No urgent actions required</p>
                </div>
            ) : (
                <div className="space-y-3">
                    {items.map((item) => (
                        <div
                            key={item.item_id}
                            className="p-4 border rounded-lg hover:bg-muted/50 transition-colors"
                        >
                            <div className="flex items-start justify-between gap-4">
                                <div className="flex-1 space-y-1">
                                    <div className="flex items-center gap-2">
                                        <Badge variant={getPriorityBadge(item.priority)} className="flex items-center gap-1">
                                            {getPriorityIcon(item.priority)}
                                            {item.priority}
                                        </Badge>
                                        <span className="text-sm font-mono text-muted-foreground">
                                            {item.deal_ref}
                                        </span>
                                    </div>

                                    <h4 className="font-semibold">{item.title}</h4>
                                    <p className="text-sm text-muted-foreground">{item.description}</p>

                                    {item.days_remaining !== null && (
                                        <p className="text-xs text-muted-foreground">
                                            {item.days_remaining < 0
                                                ? `Overdue by ${Math.abs(item.days_remaining)} days`
                                                : item.days_remaining === 0
                                                    ? 'Due today'
                                                    : `${item.days_remaining} days remaining`}
                                        </p>
                                    )}
                                </div>

                                <Button asChild variant="ghost" size="sm">
                                    <Link href={`/admin/deals/${item.item_id}`}>
                                        View ‚Üí
                                    </Link>
                                </Button>
                            </div>
                        </div>
                    ))}
                </div>
            )}
        </Card>
    )
}

'use client'

import { useEffect, useState } from 'react'
import { Briefcase, Mail, Phone, Clock } from 'lucide-react'
import { Card } from '@/components/ui/card'
import { Badge } from '@/components/ui/badge'
import { Button } from '@/components/ui/button'
import { Skeleton } from '@/components/ui/skeleton'
import { createClient } from '@/lib/supabase/client'

interface ITLead {
    id: string
    company_name: string
    contact_person: string
    email: string
    phone: string | null
    service_interest: string
    status: string
    priority: string
    created_at: string
    days_old: number
}

export function ITLeadsWidget() {
    const [leads, setLeads] = useState<ITLead[]>([])
    const [isLoading, setIsLoading] = useState(true)

    useEffect(() => {
        loadLeads()
    }, [])

    async function loadLeads() {
        try {
            const supabase = createClient()

            const { data, error } = await supabase
                .rpc('get_recent_it_leads', { p_days: 30 }) as any

            if (error) throw error

            setLeads(data || [])
        } catch (error) {
            console.error('Failed to load IT leads:', error)
        } finally {
            setIsLoading(false)
        }
    }

    const getStatusColor = (status: string) => {
        const colors = {
            'New': 'bg-green-500',
            'Contacted': 'bg-blue-500',
            'Qualified': 'bg-purple-500',
            'Proposal Sent': 'bg-amber-500',
            'Won': 'bg-emerald-500',
            'Lost': 'bg-gray-500'
        } as const
        return colors[status as keyof typeof colors] || 'bg-gray-500'
    }

    const getPriorityVariant = (priority: string) => {
        const variants = {
            'Urgent': 'destructive',
            'High': 'destructive',
            'Medium': 'secondary',
            'Low': 'outline'
        } as const
        return variants[priority as keyof typeof variants] || 'outline'
    }

    if (isLoading) {
        return (
            <Card className="p-6">
                <Skeleton className="h-8 w-48 mb-4" />
                <div className="space-y-3">
                    <Skeleton className="h-24 w-full" />
                    <Skeleton className="h-24 w-full" />
                    <Skeleton className="h-24 w-full" />
                </div>
            </Card>
        )
    }

    const newLeads = leads.filter(l => l.status === 'New')

    return (
        <Card className="p-6">
            <div className="flex items-center justify-between mb-6">
                <h3 className="text-lg font-semibold flex items-center gap-2">
                    <Briefcase className="h-5 w-5 text-indigo-600" />
                    IT Consultation Leads
                </h3>
                {newLeads.length > 0 && (
                    <Badge className="bg-green-500">{newLeads.length} new</Badge>
                )}
            </div>

            {leads.length === 0 ? (
                <div className="text-center py-12 text-muted-foreground">
                    <Briefcase className="h-12 w-12 mx-auto mb-4 opacity-50" />
                    <p className="font-medium">No recent leads</p>
                    <p className="text-sm">New consultation requests will appear here</p>
                </div>
            ) : (
                <div className="space-y-4 max-h-[600px] overflow-y-auto">
                    {leads.map((lead) => (
                        <div
                            key={lead.id}
                            className="p-4 border rounded-lg hover:bg-muted/50 transition-colors"
                        >
                            <div className="space-y-3">
                                {/* Header */}
                                <div className="flex items-start justify-between">
                                    <div className="flex-1">
                                        <h4 className="font-semibold text-lg">{lead.company_name}</h4>
                                        <p className="text-sm text-muted-foreground">{lead.contact_person}</p>
                                    </div>
                                    <div className="flex flex-col items-end gap-1">
                                        <Badge className={getStatusColor(lead.status)}>
                                            {lead.status}
                                        </Badge>
                                        <Badge variant={getPriorityVariant(lead.priority)}>
                                            {lead.priority}
                                        </Badge>
                                    </div>
                                </div>

                                {/* Service */}
                                <div className="flex items-center gap-2 text-sm">
                                    <Briefcase className="h-4 w-4 text-muted-foreground" />
                                    <span className="font-medium text-indigo-600 dark:text-indigo-400">
                                        {lead.service_interest}
                                    </span>
                                </div>

                                {/* Contact Info */}
                                <div className="space-y-1 text-sm text-muted-foreground">
                                    <div className="flex items-center gap-2">
                                        <Mail className="h-4 w-4" />
                                        <a href={`mailto:${lead.email}`} className="hover:underline">
                                            {lead.email}
                                        </a>
                                    </div>
                                    {lead.phone && (
                                        <div className="flex items-center gap-2">
                                            <Phone className="h-4 w-4" />
                                            <a href={`tel:${lead.phone}`} className="hover:underline">
                                                {lead.phone}
                                            </a>
                                        </div>
                                    )}
                                </div>

                                {/* Timestamp */}
                                <div className="flex items-center justify-between pt-2 border-t">
                                    <div className="flex items-center gap-2 text-xs text-muted-foreground">
                                        <Clock className="h-3 w-3" />
                                        <span>
                                            {lead.days_old === 0
                                                ? 'Today'
                                                : lead.days_old === 1
                                                    ? 'Yesterday'
                                                    : `${lead.days_old} days ago`}
                                        </span>
                                    </div>

                                    <div className="flex gap-2">
                                        <Button
                                            size="sm"
                                            variant="outline"
                                            onClick={() => alert(`View details for ${lead.company_name}`)}
                                        >
                                            View Details
                                        </Button>
                                        {lead.status === 'New' && (
                                            <Button
                                                size="sm"
                                                onClick={() => {
                                                    alert(`Assigned ${lead.company_name} to you`)
                                                    // In a real app, calling an API to update status
                                                }}
                                            >
                                                Assign
                                            </Button>
                                        )}
                                    </div>
                                </div>
                            </div>
                        </div>
                    ))}
                </div>
            )}
        </Card>
    )
}

'use client'

import { useEffect, useState } from 'react'
import { Ship, MapPin, Package, Calendar } from 'lucide-react'
import { Card } from '@/components/ui/card'
import { Badge } from '@/components/ui/badge'
import { Skeleton } from '@/components/ui/skeleton'
import Link from 'next/link'

interface ShipmentData {
    id: string
    deal_ref: string
    container_number: string | null
    port_of_loading: string | null
    port_of_discharge: string | null
    shipment_period_end: string | null
    current_status: string
    days_until_arrival: number | null
}

export function LogisticsTracker() {
    const [shipments, setShipments] = useState<ShipmentData[]>([])
    const [isLoading, setIsLoading] = useState(true)

    useEffect(() => {
        loadShipments()
    }, [])

    async function loadShipments() {
        try {
            // Use Server Action instead of client fetch
            const { getLogisticsShipments } = await import('@/actions/logistics')
            const data = await getLogisticsShipments()

            // Calculate days until arrival
            const shipmentsWithDays = (data || []).map((deal: any) => {
                let days_until_arrival = null
                if (deal.shipment_period_end) {
                    const today = new Date()
                    today.setHours(0, 0, 0, 0)
                    const eta = new Date(deal.shipment_period_end)
                    eta.setHours(0, 0, 0, 0)
                    days_until_arrival = Math.ceil((eta.getTime() - today.getTime()) / (1000 * 60 * 60 * 24))
                }

                return {
                    id: deal.id,
                    deal_ref: deal.deal_ref,
                    container_number: deal.container_number,
                    port_of_loading: deal.port_of_loading,
                    port_of_discharge: deal.port_of_discharge,
                    shipment_period_end: deal.shipment_period_end,
                    current_status: deal.status,
                    days_until_arrival
                }
            })

            setShipments(shipmentsWithDays)
        } catch (error) {
            console.error('Failed to load shipments:', error)
        } finally {
            setIsLoading(false)
        }
    }

    const getStatusColor = (status: string) => {
        const colors = {
            'Shipped': 'bg-blue-500',
            'In Transit': 'bg-purple-500',
            'Customs': 'bg-amber-500'
        } as const
        return colors[status as keyof typeof colors] || 'bg-gray-500'
    }

    const getETABadge = (days: number | null) => {
        if (days === null) return null

        if (days < 0) {
            return <Badge variant="destructive">Delayed ({Math.abs(days)}d)</Badge>
        }
        if (days === 0) {
            return <Badge variant="destructive">Arriving Today</Badge>
        }
        if (days <= 3) {
            return <Badge className="bg-amber-500">ETA: {days}d</Badge>
        }
        return <Badge variant="secondary">ETA: {days}d</Badge>
    }

    if (isLoading) {
        return (
            <Card className="p-6">
                <Skeleton className="h-8 w-48 mb-4" />
                <div className="space-y-3">
                    <Skeleton className="h-20 w-full" />
                    <Skeleton className="h-20 w-full" />
                    <Skeleton className="h-20 w-full" />
                </div>
            </Card>
        )
    }

    return (
        <Card className="p-6">
            <div className="flex items-center justify-between mb-6">
                <h3 className="text-lg font-semibold flex items-center gap-2">
                    <Ship className="h-5 w-5 text-blue-600" />
                    Logistics Tracker
                </h3>
                {shipments.length > 0 && (
                    <Badge variant="outline">{shipments.length} active</Badge>
                )}
            </div>

            {shipments.length === 0 ? (
                <div className="text-center py-12 text-muted-foreground">
                    <Package className="h-12 w-12 mx-auto mb-4 opacity-50" />
                    <p className="font-medium">No active shipments</p>
                    <p className="text-sm">All containers have been delivered</p>
                </div>
            ) : (
                <div className="space-y-4">
                    {shipments.map((shipment) => (
                        <Link
                            key={shipment.id}
                            href={`/admin/deals/${shipment.id}`}
                            className="block p-4 border rounded-lg hover:bg-muted/50 transition-colors"
                        >
                            <div className="space-y-3">
                                {/* Header */}
                                <div className="flex items-start justify-between">
                                    <div>
                                        <div className="flex items-center gap-2 mb-1">
                                            <Badge className={getStatusColor(shipment.current_status)}>
                                                {shipment.current_status}
                                            </Badge>
                                            {getETABadge(shipment.days_until_arrival)}
                                        </div>
                                        <p className="font-mono text-sm text-muted-foreground">
                                            {shipment.deal_ref}
                                        </p>
                                    </div>
                                </div>

                                {/* Container Info */}
                                {shipment.container_number && (
                                    <div className="flex items-center gap-2 text-sm">
                                        <Package className="h-4 w-4 text-muted-foreground" />
                                        <span className="font-semibold">{shipment.container_number}</span>
                                    </div>
                                )}

                                {/* Route */}
                                <div className="flex items-center gap-2 text-sm text-muted-foreground">
                                    <MapPin className="h-4 w-4" />
                                    <span>
                                        {shipment.port_of_loading || 'Unknown'} ‚Üí {shipment.port_of_discharge || 'Unknown'}
                                    </span>
                                </div>

                                {/* ETA */}
                                {shipment.shipment_period_end && (
                                    <div className="flex items-center gap-2 text-sm text-muted-foreground">
                                        <Calendar className="h-4 w-4" />
                                        <span>
                                            ETA: {new Date(shipment.shipment_period_end).toLocaleDateString('en-US', {
                                                month: 'short',
                                                day: 'numeric',
                                                year: 'numeric'
                                            })}
                                        </span>
                                    </div>
                                )}
                            </div>
                        </Link>
                    ))}
                </div>
            )}
        </Card>
    )
}

'use client'

import { useEffect, useState } from 'react'
import { getMetalRates, getCurrencyRates, MetalRate, CurrencyRate } from '@/app/actions/market'
import { TrendingUp, TrendingDown, DollarSign } from 'lucide-react'

export function MarketTicker() {
    const [metals, setMetals] = useState<MetalRate[]>([])
    const [currencies, setCurrencies] = useState<CurrencyRate[]>([])
    const [loading, setLoading] = useState(true)

    useEffect(() => {
        async function fetchData() {
            try {
                const [metalData, currencyData] = await Promise.all([
                    getMetalRates(),
                    getCurrencyRates()
                ])
                setMetals(metalData)
                setCurrencies(currencyData)
            } catch (error) {
                console.error("Failed to load market data", error)
            } finally {
                setLoading(false)
            }
        }
        fetchData()
    }, [])

    // Simulate "Live" ticks
    useEffect(() => {
        const interval = setInterval(() => {
            setMetals(current =>
                current.map(m => ({
                    ...m,
                    // Fluctuate by +/- 0.05%
                    price: m.price * (1 + (Math.random() * 0.001 - 0.0005))
                }))
            )
        }, 3000)

        return () => clearInterval(interval)
    }, [])

    if (loading) return <div className="h-8 bg-black/90 w-full animate-pulse" />

    return (
        <div className="w-full bg-transparent text-white overflow-hidden whitespace-nowrap border-b border-white/10 h-8 flex items-center box-border relative">
            {/* Gradient Overlays for smooth fade */}
            <div className="absolute left-0 top-0 bottom-0 w-16 bg-gradient-to-r from-black/30 to-transparent z-10 pointer-events-none" />
            <div className="absolute right-0 top-0 bottom-0 w-16 bg-gradient-to-l from-black/30 to-transparent z-10 pointer-events-none" />

            <div className="animate-marquee inline-block px-4">
                {/* Metals */}
                {metals.map((m) => (
                    <span key={m.symbol} className="mx-6 text-xs font-mono tracking-wider inline-flex items-center">
                        <span className="font-bold text-white mr-2">{m.name}</span>
                        <span className="mr-1 text-primary-foreground/90 font-medium">${m.price.toFixed(2)}</span>
                        {Math.random() > 0.5 ? (
                            <TrendingUp className="h-3 w-3 text-emerald-400" />
                        ) : (
                            <TrendingDown className="h-3 w-3 text-red-400" />
                        )}
                    </span>
                ))}

                {/* Separator */}
                <span className="mx-4 text-white/20">|</span>

                {/* Currencies */}
                {currencies.map((c) => (
                    <span key={c.code} className="mx-6 text-xs font-mono tracking-wider inline-flex items-center">
                        <span className="font-bold text-indigo-300 mr-2">1 USD =</span>
                        <span className="mr-1 text-white/90">{c.rate.toFixed(2)} {c.code}</span>
                    </span>
                ))}
            </div>

            {/* Duplicate for seamless loop */}
            <div className="animate-marquee inline-block px-4" aria-hidden="true">
                {metals.map((m) => (
                    <span key={`${m.symbol}-duplicate`} className="mx-6 text-xs font-mono tracking-wider inline-flex items-center">
                        <span className="font-bold text-white mr-2">{m.name}</span>
                        <span className="mr-1 text-primary-foreground/90 font-medium">${m.price.toFixed(2)}</span>
                        {Math.random() > 0.5 ? (
                            <TrendingUp className="h-3 w-3 text-emerald-400" />
                        ) : (
                            <TrendingDown className="h-3 w-3 text-red-400" />
                        )}
                    </span>
                ))}
                <span className="mx-4 text-white/20">|</span>
                {currencies.map((c) => (
                    <span key={`${c.code}-dup`} className="mx-6 text-xs font-mono tracking-wider inline-flex items-center">
                        <span className="font-bold text-indigo-300 mr-2">1 USD =</span>
                        <span className="mr-1 text-white/90">{c.rate.toFixed(2)} {c.code}</span>
                    </span>
                ))}
            </div>

            <style jsx>{`
                .animate-marquee {
                    animation: marquee 40s linear infinite;
                }
                @keyframes marquee {
                    0% { transform: translateX(0); }
                    100% { transform: translateX(-50%); }
                }
                .animate-marquee:hover {
                    animation-play-state: paused;
                }
            `}</style>
        </div>
    )
}

'use client'

import { useState } from 'react'
import { useRouter } from 'next/navigation'
import { useForm } from 'react-hook-form'
import { zodResolver } from '@hookform/resolvers/zod'
import {
    dealWizardSchema,
    defaultFormValues,
    DealWizardFormData,
    step1Schema,
    step2Schema,
    step2Base,
    step3Schema,
    step4Schema
} from '@/lib/validations/deal-schema'
import { useDealMasterData } from '@/hooks/useDealCalculations'
import { createClient } from '@/lib/supabase/client'
import { Button } from '@/components/ui/button'
import { Card } from '@/components/ui/card'
import { Tabs, TabsContent, TabsList, TabsTrigger } from '@/components/ui/tabs'
import { useToast } from '@/hooks/use-toast'
import { Loader2, CheckCircle2, ChevronRight, ChevronLeft } from 'lucide-react'

// Steps
import { Step1Sourcing } from './Step1Sourcing'
import { Step2Pricing } from './Step2Pricing'
import { Step3Logistics } from './Step3Logistics'
import { Step4Documents } from './Step4Documents'

const STEPS = [
    { id: 'sourcing', title: 'Sourcing', schema: step1Schema, baseSchema: step1Schema },
    { id: 'pricing', title: 'Pricing & Payment', schema: step2Schema, baseSchema: step2Base },
    { id: 'logistics', title: 'Logistics', schema: step3Schema, baseSchema: step3Schema },
    { id: 'review', title: 'Review', schema: step4Schema, baseSchema: step4Schema }
]

export function DealWizard() {
    const router = useRouter()
    const { toast } = useToast()
    const [currentStep, setCurrentStep] = useState(0)
    const [isSubmitting, setIsSubmitting] = useState(false)
    const { suppliers, commodities, isLoading: isLoadingMasterData } = useDealMasterData()

    const form = useForm<DealWizardFormData>({
        resolver: zodResolver(dealWizardSchema) as any,
        defaultValues: defaultFormValues,
        mode: 'onChange'
    })

    const handleNext = async () => {
        const step = STEPS[currentStep]
        // Use baseSchema for retrieving field keys (avoids ZodEffects .shape issue)
        const baseSchema = (step as any).baseSchema || step.schema
        const fields = Object.keys(baseSchema.shape) as any[]

        console.log('=== handleNext Debug ===')
        console.log('Current Step:', currentStep, '-', step.title)
        console.log('Fields to validate:', fields)
        console.log('Current form values:', form.getValues())

        // Trigger basic field validation (UI feedback)
        const isBaseValid = await form.trigger(fields)
        console.log('Base validation result:', isBaseValid)

        // Perform full schema validation (including refinements like Sum=100%)
        const fullValidation = step.schema.safeParse(form.getValues())
        const isValid = isBaseValid && fullValidation.success

        console.log('Full validation success:', fullValidation.success)

        if (isValid) {
            console.log('‚úÖ Validation passed, advancing to next step')
            setCurrentStep((prev) => Math.min(prev + 1, STEPS.length - 1))
        } else {
            console.log('‚ùå Validation failed')

            // If base validation passed but full validation failed (refinement errors)
            if (isBaseValid && !fullValidation.success) {
                console.log('Refinement errors found:', fullValidation.error.issues)
                fullValidation.error.issues.forEach((issue) => {
                    if (issue.path.length > 0) {
                        const fieldName = issue.path[0] as any
                        form.setError(fieldName, {
                            type: 'manual',
                            message: issue.message
                        })
                    }
                })
            }

            toast({
                title: 'Validation Error',
                description: 'Please fix the errors in the form before proceeding.',
                variant: 'destructive'
            })
        }
    }

    const handleBack = () => {
        setCurrentStep((prev) => Math.max(prev - 1, 0))
    }

    const onSubmit = async (data: DealWizardFormData) => {
        if (isSubmitting) return
        setIsSubmitting(true)
        try {
            const supabase = createClient()

            // Generate sequential Ref (mock logic for now, or DB trigger)
            const dealRef = `D-${new Date().getFullYear()}-${Math.floor(1000 + Math.random() * 9000)}`

            const { error } = await supabase.from('deals').insert({
                deal_ref: dealRef,
                status: 'Draft',
                trader_id: (await supabase.auth.getUser()).data.user?.id!,
                partner_id: data.partnerId,
                commodity_id: data.commodityId,
                origin_country: data.originCountry,
                validity_date: data.validityDate || null,
                incoterm: data.incoterm,

                // Pricing
                buy_price: data.buyPrice,
                currency: data.currency,
                weight_mt: data.weightMT,
                exchange_rate_locked: data.customsExchangeRate,

                // Logistics
                logistics_type: 'Self_Managed',
                destination_type: 'Warehouse',
                port_of_loading: data.portOfLoading,
                port_of_discharge: data.portOfDischarge,
                shipment_period_start: data.shipmentPeriodStart || null,
                shipment_period_end: data.shipmentPeriodEnd || null,
                partial_shipment_allowed: data.partialShipment,
                transshipment_allowed: data.transshipment,
                free_days_detention: data.freeDays,
                shipping_line: data.shippingLine,
                freight_forwarder: data.freightForwarder,
                pol: data.portOfLoading, // Redundant but requested in schema
                pod: data.portOfDischarge, // Redundant but requested in schema

                // Payment
                payment_method: data.paymentMethod,
                payment_terms: data.paymentTerms,
                advance_percentage: data.advancePercent,
                balance_percentage: data.balancePercent,
                lc_number: data.lcNumber || null,
                lc_expiry_date: data.lcExpiryDate || null,
                issuing_bank: data.issuingBank || null,

                // Product Quality & Specs
                packing_type: data.packagingType || null,
                isri_code: data.isriCode,
                guaranteed_recovery_rate: data.guaranteedRecovery,
                moisture_tolerance: data.moistureTolerance,
                dust_tolerance: data.dustTolerance,
                quantity_tolerance_percent: data.quantityTolerance || 0,
                quality_specs: data.qualitySpecs,

                // Agreement
                claims_days: data.claimsDays,
                weight_franchise: data.weightFranchise,

                // Costing (Estimates)
                cost_ocean_freight: data.oceanFreight || 0,
                cost_insurance: data.insurance || 0,
                cost_customs_rate: data.customsExchangeRate,
                cost_bcd_percent: data.bcdPercent,
                cost_sws_percent: data.swsPercent,
                cost_finance: data.financeCost,

                // Docs
                required_documents: data.requiredDocuments,
                notes: data.notes
            } as any)

            if (error) throw error

            // Create Payment Records
            const dealId = (data as any)[0]?.id // Assuming Supabase returns created object if .select() is used

            // Re-fetch deal ID if not returned above (insert doesn't return data by default without select)
            const { data: createdDeal, error: fetchError } = await supabase
                .from('deals')
                .select('id, total_landed_cost_inr')
                .eq('deal_ref', dealRef)
                .single()

            if (fetchError || !createdDeal) {
                console.error('Failed to fetch created deal for payments:', fetchError)
                // Don't block flow, but log error
            } else {
                const payments = []
                const totalCost = createdDeal.total_landed_cost_inr || 0 // Use estimated cost for now, or buy_price * quantity

                // 1. Advance Payment
                if (data.advancePercent > 0) {
                    payments.push({
                        deal_id: createdDeal.id,
                        payment_type: 'Advance',
                        percentage: data.advancePercent,
                        amount_usd: (data.buyPrice * data.weightMT * (data.advancePercent / 100)), // Approximate
                        due_date: new Date().toISOString(), // Due immediately
                        status: 'Pending',
                        payment_method: data.paymentMethod,
                        created_by: (await supabase.auth.getUser()).data.user?.id!
                    })
                }

                // 2. Balance Payment
                if (data.balancePercent > 0) {
                    payments.push({
                        deal_id: createdDeal.id,
                        payment_type: 'Balance',
                        percentage: data.balancePercent,
                        amount_usd: (data.buyPrice * data.weightMT * (data.balancePercent / 100)),
                        due_date: data.shipmentPeriodEnd || new Date().toISOString(), // Due on shipment/arrival
                        status: 'Pending',
                        payment_method: data.paymentMethod,
                        created_by: (await supabase.auth.getUser()).data.user?.id!
                    })
                }

                if (payments.length > 0) {
                    const { error: payError } = await supabase.from('deal_payments').insert(payments)
                    if (payError) console.error('Failed to create payment records:', payError)
                }
            }

            toast({
                title: 'Deal Created Successfully',
                description: `Reference: ${dealRef}`,
            })

            router.push('/admin/deals')

        } catch (error: any) {
            console.error('Submission error:', error)
            toast({
                title: 'Error Creating Deal',
                description: error.message,
                variant: 'destructive'
            })
        } finally {
            setIsSubmitting(false)
        }
    }

    if (isLoadingMasterData) {
        return (
            <div className="flex items-center justify-center p-12">
                <Loader2 className="h-8 w-8 animate-spin text-primary" />
            </div>
        )
    }

    return (
        <div className="max-w-4xl mx-auto py-8">
            <h1 className="text-3xl font-bold mb-8">Create New Deal</h1>

            {/* Step Indicators */}
            <div className="flex justify-between mb-8 relative">
                <div className="absolute top-1/2 left-0 w-full h-1 bg-muted -z-10" />
                {STEPS.map((step, index) => {
                    const isCompleted = index < currentStep
                    const isCurrent = index === currentStep

                    return (
                        <div key={step.id} className="flex flex-col items-center bg-background px-2">
                            <div
                                className={`
                                    w-10 h-10 rounded-full flex items-center justify-center border-2 
                                    ${isCompleted ? 'bg-primary border-primary text-primary-foreground' :
                                        isCurrent ? 'border-primary text-primary' : 'border-muted text-muted-foreground'}
                                    transition-colors duration-200
                                `}
                            >
                                {isCompleted ? <CheckCircle2 className="h-6 w-6" /> : <span>{index + 1}</span>}
                            </div>
                            <span className={`mt-2 text-sm font-medium ${isCurrent ? 'text-foreground' : 'text-muted-foreground'}`}>
                                {step.title}
                            </span>
                        </div>
                    )
                })}
            </div>

            {/* Form Content */}
            <Card className="p-6 min-h-[500px]">
                <form onSubmit={form.handleSubmit(onSubmit)}>
                    <div className="mb-8">
                        {currentStep === 0 && (
                            <Step1Sourcing
                                form={form}
                                suppliers={suppliers}
                                commodities={commodities}
                            />
                        )}
                        {currentStep === 1 && (
                            <Step2Pricing form={form} />
                        )}
                        {currentStep === 2 && (
                            <Step3Logistics form={form} />
                        )}
                        {currentStep === 3 && (
                            <Step4Documents
                                form={form}
                                // Pass commodity name for calculator context
                                commodityName={commodities.find(c => c.id === form.getValues('commodityId'))?.name}
                            />
                        )}
                    </div>

                    {/* Navigation Buttons */}
                    <div className="flex justify-between pt-4 border-t">
                        <Button
                            type="button"
                            variant="outline"
                            onClick={handleBack}
                            disabled={currentStep === 0 || isSubmitting}
                        >
                            <ChevronLeft className="h-4 w-4 mr-2" />
                            Back
                        </Button>

                        {currentStep < STEPS.length - 1 ? (
                            <Button
                                type="button"
                                onClick={handleNext}
                            >
                                Next Step
                                <ChevronRight className="h-4 w-4 ml-2" />
                            </Button>
                        ) : (
                            <Button
                                type="submit"
                                className="bg-green-600 hover:bg-green-700"
                                data-testid="submit-deal-button"
                            >
                                {isSubmitting ? (
                                    <>
                                        <Loader2 className="h-4 w-4 mr-2 animate-spin" />
                                        Creating...
                                    </>
                                ) : (
                                    'Create Deal'
                                )}
                            </Button>
                        )}
                    </div>
                </form>
            </Card>
        </div>
    )
}

'use client'

import { useState, useEffect, useCallback } from 'react'
import { Download, Trash2, FileText, Loader2, AlertCircle } from 'lucide-react'
import { Button } from '@/components/ui/button'
import { Card } from '@/components/ui/card'
import { Alert, AlertDescription } from '@/components/ui/alert'
import { Badge } from '@/components/ui/badge'
import {
    AlertDialog,
    AlertDialogAction,
    AlertDialogCancel,
    AlertDialogContent,
    AlertDialogDescription,
    AlertDialogFooter,
    AlertDialogHeader,
    AlertDialogTitle,
    AlertDialogTrigger,
} from '@/components/ui/alert-dialog'
import {
    getDealDocuments,
    getDocumentDownloadUrl,
    deleteDocument,
    getDocumentIcon,
    formatFileSize,
    type DocumentMetadata
} from '@/lib/storage/document-storage'
import { useToast } from '@/hooks/use-toast'

interface DocumentListProps {
    dealId: string
    isAdmin?: boolean
    refreshTrigger?: number
}

interface GroupedDocuments {
    [tag: string]: DocumentMetadata[]
}

export function DocumentList({ dealId, isAdmin = false, refreshTrigger = 0 }: DocumentListProps) {
    const [documents, setDocuments] = useState<DocumentMetadata[]>([])
    const [isLoading, setIsLoading] = useState(true)
    const [error, setError] = useState<string | null>(null)
    const [downloadingId, setDownloadingId] = useState<string | null>(null)
    const { toast } = useToast()

    const loadDocuments = useCallback(async () => {
        setIsLoading(true)
        setError(null)

        const result = await getDealDocuments(dealId)

        if (result.error) {
            setError(result.error)
            setDocuments([])
        } else {
            setDocuments(result.documents || [])
        }

        setIsLoading(false)
    }, [dealId])

    useEffect(() => {
        loadDocuments()
    }, [dealId, refreshTrigger, loadDocuments])

    const handleDownload = async (doc: DocumentMetadata) => {
        setDownloadingId(doc.id)

        try {
            const result = await getDocumentDownloadUrl(doc.file_path)

            if (result.error) {
                throw new Error(result.error)
            }

            if (result.url) {
                // Open in new tab
                window.open(result.url, '_blank')

                toast({
                    title: 'Download Started',
                    description: `Opening ${doc.file_name}`
                })
            }
        } catch (error) {
            toast({
                title: 'Download Failed',
                description: error instanceof Error ? error.message : 'Please try again',
                variant: 'destructive'
            })
        } finally {
            setDownloadingId(null)
        }
    }

    const handleDelete = async (docId: string, fileName: string) => {
        try {
            const result = await deleteDocument(docId)

            if (result.success) {
                toast({
                    title: 'Document Deleted',
                    description: `${fileName} has been removed`
                })
                loadDocuments() // Refresh list
            } else {
                throw new Error(result.error || 'Delete failed')
            }
        } catch (error) {
            toast({
                title: 'Delete Failed',
                description: error instanceof Error ? error.message : 'Please try again',
                variant: 'destructive'
            })
        }
    }

    // Group documents by tag
    const groupedDocuments: GroupedDocuments = documents.reduce((acc, doc) => {
        if (!acc[doc.document_tag]) {
            acc[doc.document_tag] = []
        }
        acc[doc.document_tag].push(doc)
        return acc
    }, {} as GroupedDocuments)

    if (isLoading) {
        return (
            <Card className="p-6">
                <div className="flex items-center justify-center py-8">
                    <Loader2 className="h-8 w-8 animate-spin text-muted-foreground" />
                </div>
            </Card>
        )
    }

    if (error) {
        return (
            <Card className="p-6">
                <Alert variant="destructive">
                    <AlertCircle className="h-4 w-4" />
                    <AlertDescription>{error}</AlertDescription>
                </Alert>
            </Card>
        )
    }

    if (documents.length === 0) {
        return (
            <Card className="p-6">
                <div className="text-center py-8">
                    <FileText className="h-12 w-12 mx-auto mb-4 text-muted-foreground" />
                    <p className="text-sm font-medium text-muted-foreground">
                        No documents uploaded yet
                    </p>
                    <p className="text-xs text-muted-foreground mt-1">
                        Upload your first document using the form above
                    </p>
                </div>
            </Card>
        )
    }

    return (
        <Card className="p-6">
            <div className="flex items-center justify-between mb-4">
                <h3 className="text-lg font-semibold">
                    Deal Documents ({documents.length})
                </h3>
            </div>

            <div className="space-y-4">
                {Object.entries(groupedDocuments).map(([tag, docs]) => (
                    <div key={tag} className="space-y-2">
                        {/* Tag Header */}
                        <div className="flex items-center gap-2 pb-2 border-b">
                            <span className="text-xl">{getDocumentIcon(tag as any)}</span>
                            <h4 className="font-semibold text-sm">{tag}</h4>
                            <Badge variant="secondary" className="ml-auto">
                                {docs.length}
                            </Badge>
                        </div>

                        {/* Documents in this tag */}
                        <div className="space-y-2 pl-6">
                            {docs.map((doc) => (
                                <div
                                    key={doc.id}
                                    className="flex items-center justify-between p-3 rounded-lg border bg-muted/30 hover:bg-muted/50 transition-colors"
                                >
                                    <div className="flex-1 min-w-0">
                                        <div className="flex items-center gap-2">
                                            <p className="text-sm font-medium truncate">
                                                {doc.file_name}
                                            </p>
                                            {doc.document_tag === 'Bill of Entry' && (
                                                <Badge variant="default" className="text-xs">
                                                    Required
                                                </Badge>
                                            )}
                                        </div>
                                        <div className="flex items-center gap-3 mt-1 text-xs text-muted-foreground">
                                            <span>{formatFileSize(doc.file_size)}</span>
                                            <span>‚Ä¢</span>
                                            <span>
                                                {new Date(doc.uploaded_at).toLocaleDateString()}
                                            </span>
                                            {(doc as any).uploader?.full_name && (
                                                <>
                                                    <span>‚Ä¢</span>
                                                    <span>{(doc as any).uploader.full_name}</span>
                                                </>
                                            )}
                                        </div>
                                        {doc.notes && (
                                            <p className="text-xs text-muted-foreground mt-1 italic">
                                                {doc.notes}
                                            </p>
                                        )}
                                    </div>

                                    <div className="flex items-center gap-2 ml-4">
                                        <Button
                                            variant="outline"
                                            size="sm"
                                            onClick={() => handleDownload(doc)}
                                            disabled={downloadingId === doc.id}
                                        >
                                            {downloadingId === doc.id ? (
                                                <Loader2 className="h-4 w-4 animate-spin" />
                                            ) : (
                                                <Download className="h-4 w-4" />
                                            )}
                                        </Button>

                                        {isAdmin && (
                                            <AlertDialog>
                                                <AlertDialogTrigger asChild>
                                                    <Button
                                                        variant="outline"
                                                        size="sm"
                                                        className="text-destructive hover:text-destructive"
                                                    >
                                                        <Trash2 className="h-4 w-4" />
                                                    </Button>
                                                </AlertDialogTrigger>
                                                <AlertDialogContent>
                                                    <AlertDialogHeader>
                                                        <AlertDialogTitle>Delete Document</AlertDialogTitle>
                                                        <AlertDialogDescription>
                                                            Are you sure you want to delete "{doc.file_name}"?
                                                            This action cannot be undone.
                                                        </AlertDialogDescription>
                                                    </AlertDialogHeader>
                                                    <AlertDialogFooter>
                                                        <AlertDialogCancel>Cancel</AlertDialogCancel>
                                                        <AlertDialogAction
                                                            onClick={() => handleDelete(doc.id, doc.file_name)}
                                                            className="bg-destructive hover:bg-destructive/90"
                                                        >
                                                            Delete
                                                        </AlertDialogAction>
                                                    </AlertDialogFooter>
                                                </AlertDialogContent>
                                            </AlertDialog>
                                        )}
                                    </div>
                                </div>
                            ))}
                        </div>
                    </div>
                ))}
            </div>
        </Card>
    )
}

'use client'

import { useState, useCallback, useRef } from 'react'
import { Upload, File, X, Loader2, CheckCircle2 } from 'lucide-react'
import { Button } from '@/components/ui/button'
import { Label } from '@/components/ui/label'
import {
    Select,
    SelectContent,
    SelectItem,
    SelectTrigger,
    SelectValue,
} from '@/components/ui/select'
import { Textarea } from '@/components/ui/textarea'
import { Card } from '@/components/ui/card'
import { Progress } from '@/components/ui/progress'
import { Alert, AlertDescription } from '@/components/ui/alert'
import {
    uploadDocument,
    validateFile,
    DOCUMENT_TAGS,
    type DocumentTag,
    formatFileSize
} from '@/lib/storage/document-storage'
import { useToast } from '@/hooks/use-toast'

interface DocumentUploaderProps {
    dealId: string
    userId: string
    onUploadComplete: () => void
}

export function DocumentUploader({ dealId, userId, onUploadComplete }: DocumentUploaderProps) {
    const [selectedFile, setSelectedFile] = useState<File | null>(null)
    const [documentTag, setDocumentTag] = useState<DocumentTag | ''>('')
    const [notes, setNotes] = useState('')
    const [isUploading, setIsUploading] = useState(false)
    const [uploadProgress, setUploadProgress] = useState(0)
    const [dragActive, setDragActive] = useState(false)
    const fileInputRef = useRef<HTMLInputElement>(null)
    const { toast } = useToast()

    const handleDrag = useCallback((e: React.DragEvent) => {
        e.preventDefault()
        e.stopPropagation()
        if (e.type === 'dragenter' || e.type === 'dragover') {
            setDragActive(true)
        } else if (e.type === 'dragleave') {
            setDragActive(false)
        }
    }, [])

    const handleFileSelect = useCallback((file: File) => {
        const validation = validateFile(file)
        if (!validation.valid) {
            toast({
                title: 'Invalid File',
                description: validation.error,
                variant: 'destructive'
            })
            return
        }
        setSelectedFile(file)
    }, [toast])

    const handleDrop = useCallback((e: React.DragEvent) => {
        e.preventDefault()
        e.stopPropagation()
        setDragActive(false)

        if (e.dataTransfer.files && e.dataTransfer.files[0]) {
            handleFileSelect(e.dataTransfer.files[0])
        }
    }, [handleFileSelect])

    const handleFileInputChange = (e: React.ChangeEvent<HTMLInputElement>) => {
        if (e.target.files && e.target.files[0]) {
            handleFileSelect(e.target.files[0])
        }
    }

    const handleUpload = async () => {
        if (!selectedFile || !documentTag) {
            toast({
                title: 'Missing Information',
                description: 'Please select a file and document type',
                variant: 'destructive'
            })
            return
        }

        setIsUploading(true)
        setUploadProgress(0)

        // Simulate progress for better UX
        const progressInterval = setInterval(() => {
            setUploadProgress(prev => Math.min(prev + 10, 90))
        }, 200)

        try {
            const result = await uploadDocument(
                {
                    dealId,
                    file: selectedFile,
                    documentTag: documentTag as DocumentTag,
                    notes: notes || undefined
                },
                userId
            )

            clearInterval(progressInterval)
            setUploadProgress(100)

            if (result.success) {
                toast({
                    title: 'Upload Successful',
                    description: `${selectedFile.name} has been uploaded`
                })

                // Reset form
                setSelectedFile(null)
                setDocumentTag('')
                setNotes('')
                setUploadProgress(0)
                if (fileInputRef.current) {
                    fileInputRef.current.value = ''
                }

                // Notify parent
                onUploadComplete()
            } else {
                throw new Error(result.error || 'Upload failed')
            }
        } catch (error) {
            clearInterval(progressInterval)
            toast({
                title: 'Upload Failed',
                description: error instanceof Error ? error.message : 'Please try again',
                variant: 'destructive'
            })
        } finally {
            setIsUploading(false)
            setTimeout(() => setUploadProgress(0), 1000)
        }
    }

    const handleRemoveFile = () => {
        setSelectedFile(null)
        if (fileInputRef.current) {
            fileInputRef.current.value = ''
        }
    }

    return (
        <Card className="p-6">
            <h3 className="text-lg font-semibold mb-4">Upload Document</h3>

            <div className="space-y-4">
                {/* Drag & Drop Area */}
                <div
                    className={`border-2 border-dashed rounded-lg p-8 text-center transition-colors ${dragActive
                        ? 'border-primary bg-primary/5'
                        : 'border-muted-foreground/25 hover:border-primary/50'
                        }`}
                    onDragEnter={handleDrag}
                    onDragLeave={handleDrag}
                    onDragOver={handleDrag}
                    onDrop={handleDrop}
                >
                    <Upload className="h-12 w-12 mx-auto mb-4 text-muted-foreground" />
                    <p className="text-sm font-medium mb-2">
                        Drag and drop your file here, or click to browse
                    </p>
                    <p className="text-xs text-muted-foreground mb-4">
                        PDF, JPG, PNG, Excel ‚Ä¢ Max 10MB
                    </p>
                    <Button
                        type="button"
                        variant="outline"
                        onClick={() => fileInputRef.current?.click()}
                        disabled={isUploading}
                    >
                        Browse Files
                    </Button>
                    <input
                        ref={fileInputRef}
                        type="file"
                        className="hidden"
                        accept=".pdf,.jpg,.jpeg,.png,.xls,.xlsx"
                        onChange={handleFileInputChange}
                        disabled={isUploading}
                    />
                </div>

                {/* Selected File */}
                {selectedFile && (
                    <Alert>
                        <div className="flex items-center justify-between">
                            <div className="flex items-center gap-3">
                                <File className="h-4 w-4 text-blue-500" />
                                <div>
                                    <p className="text-sm font-medium">{selectedFile.name}</p>
                                    <p className="text-xs text-muted-foreground">
                                        {formatFileSize(selectedFile.size)}
                                    </p>
                                </div>
                            </div>
                            {!isUploading && (
                                <Button
                                    variant="ghost"
                                    size="icon"
                                    onClick={handleRemoveFile}
                                >
                                    <X className="h-4 w-4" />
                                </Button>
                            )}
                        </div>
                    </Alert>
                )}

                {/* Document Type */}
                <div className="space-y-2">
                    <Label htmlFor="documentTag">Document Type *</Label>
                    <Select
                        value={documentTag}
                        onValueChange={(value) => setDocumentTag(value as DocumentTag)}
                        disabled={isUploading}
                    >
                        <SelectTrigger id="documentTag">
                            <SelectValue placeholder="Select document type..." />
                        </SelectTrigger>
                        <SelectContent>
                            {DOCUMENT_TAGS.map((tag) => (
                                <SelectItem key={tag} value={tag}>
                                    {tag}
                                </SelectItem>
                            ))}
                        </SelectContent>
                    </Select>
                </div>

                {/* Notes */}
                <div className="space-y-2">
                    <Label htmlFor="notes">Notes (Optional)</Label>
                    <Textarea
                        id="notes"
                        placeholder="Add any additional notes about this document..."
                        value={notes}
                        onChange={(e) => setNotes(e.target.value)}
                        disabled={isUploading}
                        rows={3}
                    />
                </div>

                {/* Upload Progress */}
                {isUploading && (
                    <div className="space-y-2">
                        <div className="flex items-center justify-between text-sm">
                            <span className="text-muted-foreground">Uploading...</span>
                            <span className="font-medium">{uploadProgress}%</span>
                        </div>
                        <Progress value={uploadProgress} className="h-2" />
                    </div>
                )}

                {/* Upload Button */}
                <Button
                    onClick={handleUpload}
                    disabled={!selectedFile || !documentTag || isUploading}
                    className="w-full"
                >
                    {isUploading ? (
                        <>
                            <Loader2 className="h-4 w-4 mr-2 animate-spin" />
                            Uploading...
                        </>
                    ) : uploadProgress === 100 ? (
                        <>
                            <CheckCircle2 className="h-4 w-4 mr-2" />
                            Uploaded
                        </>
                    ) : (
                        <>
                            <Upload className="h-4 w-4 mr-2" />
                            Upload Document
                        </>
                    )}
                </Button>
            </div>
        </Card>
    )
}

'use client'

import { useEffect, useState } from 'react'
import { CheckCircle2, Circle, AlertCircle } from 'lucide-react'
import { Card } from '@/components/ui/card'
import { Badge } from '@/components/ui/badge'
import { Alert, AlertDescription } from '@/components/ui/alert'
import { hasRequiredDocument, getDocumentIcon, type DocumentTag } from '@/lib/storage/document-storage'

interface RequiredDocument {
    tag: DocumentTag
    requiredForStatus: string
    description: string
}

const REQUIRED_DOCUMENTS: RequiredDocument[] = [
    {
        tag: 'Bill of Lading',
        requiredForStatus: 'Shipped',
        description: 'Proof of shipment from origin port'
    },
    {
        tag: 'Commercial Invoice',
        requiredForStatus: 'Approved',
        description: 'Invoice from supplier with pricing details'
    },
    {
        tag: 'Bill of Entry',
        requiredForStatus: 'Customs',
        description: 'Required for customs clearance'
    },
    {
        tag: 'Weight Slip',
        requiredForStatus: 'Shipped',
        description: 'Weight verification at origin'
    }
]

interface RequiredDocsChecklistProps {
    dealId: string
    currentStatus: string
    refreshTrigger?: number
}

export function RequiredDocsChecklist({
    dealId,
    currentStatus,
    refreshTrigger = 0
}: RequiredDocsChecklistProps) {
    const [documentStatus, setDocumentStatus] = useState<Record<DocumentTag, boolean>>({} as any)
    const [isLoading, setIsLoading] = useState(true)

    useEffect(() => {
        async function checkDocuments() {
            setIsLoading(true)
            const status: Record<string, boolean> = {}

            for (const doc of REQUIRED_DOCUMENTS) {
                const exists = await hasRequiredDocument(dealId, doc.tag)
                status[doc.tag] = exists
            }

            setDocumentStatus(status as any)
            setIsLoading(false)
        }

        checkDocuments()
    }, [dealId, refreshTrigger])

    const getStatusIcon = (tag: DocumentTag) => {
        if (isLoading) {
            return <Circle className="h-5 w-5 text-muted-foreground animate-pulse" />
        }

        return documentStatus[tag] ? (
            <CheckCircle2 className="h-5 w-5 text-green-500" />
        ) : (
            <Circle className="h-5 w-5 text-muted-foreground" />
        )
    }

    const missingDocs = REQUIRED_DOCUMENTS.filter((doc) => !documentStatus[doc.tag])
    const isBlocked = missingDocs.some((doc) => {
        // Check if we're trying to reach a status that requires this document
        const statusOrder = ['Draft', 'Approved', 'Shipped', 'Customs', 'Stock', 'Sold']
        const currentIndex = statusOrder.indexOf(currentStatus)
        const requiredIndex = statusOrder.indexOf(doc.requiredForStatus)
        return currentIndex >= requiredIndex
    })

    return (
        <Card className="p-6">
            <div className="flex items-center justify-between mb-4">
                <h3 className="text-lg font-semibold">Required Documents</h3>
                <Badge variant={missingDocs.length === 0 ? 'default' : 'secondary'}>
                    {REQUIRED_DOCUMENTS.length - missingDocs.length} / {REQUIRED_DOCUMENTS.length}
                </Badge>
            </div>

            {isBlocked && (
                <Alert className="mb-4 border-amber-500/50 bg-amber-500/10">
                    <AlertCircle className="h-4 w-4 text-amber-500" />
                    <AlertDescription className="text-amber-700 dark:text-amber-400">
                        <strong>Action Required:</strong> Some required documents are missing for the current deal status.
                    </AlertDescription>
                </Alert>
            )}

            <div className="space-y-3">
                {REQUIRED_DOCUMENTS.map((doc) => {
                    const isUploaded = documentStatus[doc.tag]
                    const isRequired = currentStatus === doc.requiredForStatus ||
                        (currentStatus === 'Stock' && doc.tag === 'Bill of Entry') ||
                        (currentStatus === 'Sold' && doc.tag === 'Bill of Entry')

                    return (
                        <div
                            key={doc.tag}
                            className={`flex items-start gap-3 p-3 rounded-lg border transition-colors ${isUploaded
                                    ? 'bg-green-500/5 border-green-500/20'
                                    : isRequired
                                        ? 'bg-amber-500/5 border-amber-500/20'
                                        : 'bg-muted/30 border-transparent'
                                }`}
                        >
                            {getStatusIcon(doc.tag)}
                            <div className="flex-1 min-w-0">
                                <div className="flex items-center gap-2">
                                    <span className="text-lg">{getDocumentIcon(doc.tag)}</span>
                                    <p className="text-sm font-medium">{doc.tag}</p>
                                    {isRequired && !isUploaded && (
                                        <Badge variant="destructive" className="text-xs">
                                            Required
                                        </Badge>
                                    )}
                                </div>
                                <p className="text-xs text-muted-foreground mt-1">
                                    {doc.description}
                                </p>
                                <p className="text-xs text-muted-foreground mt-1">
                                    Required for: <span className="font-medium">{doc.requiredForStatus}</span>
                                </p>
                            </div>
                            {isUploaded && (
                                <CheckCircle2 className="h-5 w-5 text-green-500 flex-shrink-0" />
                            )}
                        </div>
                    )
                })}
            </div>

            {missingDocs.length === 0 && !isLoading && (
                <Alert className="mt-4 border-green-500/50 bg-green-500/10">
                    <CheckCircle2 className="h-4 w-4 text-green-500" />
                    <AlertDescription className="text-green-700 dark:text-green-400">
                        All required documents have been uploaded!
                    </AlertDescription>
                </Alert>
            )}
        </Card>
    )
}

'use client'

import { useEffect, useState } from 'react'
import { TrendingUp, TrendingDown, AlertTriangle, DollarSign } from 'lucide-react'
import { Card } from '@/components/ui/card'
import { Badge } from '@/components/ui/badge'
import { Progress } from '@/components/ui/progress'
import { Skeleton } from '@/components/ui/skeleton'
import { Alert, AlertDescription } from '@/components/ui/alert'
import {
    formatCurrency,
    formatPercentage,
    getVarianceLevel,
    getProfitMarginLevel,
    type PLSummary
} from '@/lib/finance/payment-calculations'
import { createClient } from '@/lib/supabase/client'

interface PLSummaryCardProps {
    dealId: string
    refreshTrigger?: number
}

export function PLSummaryCard({ dealId, refreshTrigger = 0 }: PLSummaryCardProps) {
    const [plData, setPlData] = useState<PLSummary | null>(null)
    const [isLoading, setIsLoading] = useState(true)
    const [error, setError] = useState<string | null>(null)

    useEffect(() => {
        async function fetchPLData() {
            setIsLoading(true)
            setError(null)

            try {
                const supabase = createClient()

                // Call the P&L summary function
                const { data, error: fetchError } = await supabase
                    .rpc('get_deal_pl_summary', { p_deal_id: dealId })
                    .single() as any

                if (fetchError) {
                    throw fetchError
                }

                setPlData(data)
            } catch (err) {
                console.error('Failed to fetch P&L data:', err)
                setError(err instanceof Error ? err.message : 'Failed to load P&L data')
            } finally {
                setIsLoading(false)
            }
        }

        fetchPLData()
    }, [dealId, refreshTrigger])

    if (isLoading) {
        return (
            <Card className="p-6">
                <Skeleton className="h-8 w-48 mb-4" />
                <div className="space-y-3">
                    <Skeleton className="h-4 w-full" />
                    <Skeleton className="h-4 w-full" />
                    <Skeleton className="h-4 w-3/4" />
                </div>
            </Card>
        )
    }

    if (error || !plData) {
        return (
            <Card className="p-6">
                <Alert variant="destructive">
                    <AlertTriangle className="h-4 w-4" />
                    <AlertDescription>{error || 'No P&L data available'}</AlertDescription>
                </Alert>
            </Card>
        )
    }

    const varianceLevel = getVarianceLevel(plData.variance_percentage)
    const profitLevel = getProfitMarginLevel(plData.profit_margin_percentage)

    const varianceColor = {
        positive: 'text-green-600 dark:text-green-400',
        neutral: 'text-blue-600 dark:text-blue-400',
        warning: 'text-amber-600 dark:text-amber-400',
        danger: 'text-red-600 dark:text-red-400'
    }[varianceLevel]

    const profitColor = {
        excellent: 'text-green-600 dark:text-green-400',
        good: 'text-blue-600 dark:text-blue-400',
        acceptable: 'text-amber-600 dark:text-amber-400',
        poor: 'text-orange-600 dark:text-orange-400',
        loss: 'text-red-600 dark:text-red-400'
    }[profitLevel]

    return (
        <Card className="p-6 bg-gradient-to-br from-slate-50 to-slate-100 dark:from-slate-900 dark:to-slate-800 border-slate-200 dark:border-slate-700">
            <div className="flex items-center justify-between mb-6">
                <h3 className="text-lg font-semibold flex items-center gap-2">
                    <DollarSign className="h-5 w-5 text-green-600" />
                    Profit & Loss Summary
                </h3>
                <Badge variant="outline">{plData.deal_ref}</Badge>
            </div>

            {/* Cost Analysis */}
            <div className="space-y-4 mb-6">
                <div className="flex justify-between items-center pb-3 border-b">
                    <span className="text-sm text-muted-foreground">Estimated Cost:</span>
                    <span className="font-semibold">{formatCurrency(plData.estimated_cost_inr)}</span>
                </div>

                <div className="flex justify-between items-center pb-3 border-b">
                    <span className="text-sm text-muted-foreground">Actual Cost:</span>
                    <div className="flex items-center gap-2">
                        <span className="font-semibold">{formatCurrency(plData.actual_cost_inr)}</span>
                        {plData.variance_percentage !== 0 && (
                            <Badge variant="outline" className={varianceColor}>
                                {plData.variance_percentage > 0 ? (
                                    <TrendingDown className="h-3 w-3 mr-1" />
                                ) : (
                                    <TrendingUp className="h-3 w-3 mr-1" />
                                )}
                                {formatPercentage(Math.abs(plData.variance_percentage))}
                            </Badge>
                        )}
                    </div>
                </div>

                <div className="flex justify-between items-center">
                    <span className="text-sm font-medium">Variance:</span>
                    <span className={`font-bold ${varianceColor}`}>
                        {plData.variance_inr >= 0 ? '+' : '-'}
                        {formatCurrency(Math.abs(plData.variance_inr))}
                        {plData.variance_inr >= 0 ? ' ‚úÖ' : ' ‚ö†Ô∏è'}
                    </span>
                </div>

                {varianceLevel === 'danger' && (
                    <Alert className="mt-4 border-red-500/50 bg-red-500/10">
                        <AlertTriangle className="h-4 w-4 text-red-500" />
                        <AlertDescription className="text-red-700 dark:text-red-400">
                            <strong>Over Budget:</strong> Actual costs exceed estimated by{' '}
                            {formatPercentage(Math.abs(plData.variance_percentage))}
                        </AlertDescription>
                    </Alert>
                )}
            </div>

            {/* Profit Analysis */}
            <div className="space-y-4 pt-4 border-t-2">
                <div className="flex justify-between items-center">
                    <span className="text-sm text-muted-foreground">Sell Price:</span>
                    <span className="font-semibold text-primary">{formatCurrency(plData.sell_price_inr)}</span>
                </div>

                <div className="flex justify-between items-center">
                    <span className="text-sm font-medium">Projected Profit:</span>
                    <span className={`font-bold text-lg ${profitColor}`}>
                        {formatCurrency(plData.profit_inr)}
                    </span>
                </div>

                <div>
                    <div className="flex justify-between items-center mb-2">
                        <span className="text-sm font-medium">Profit Margin:</span>
                        <span className={`font-bold ${profitColor}`}>
                            {formatPercentage(plData.profit_margin_percentage)}
                            {profitLevel === 'excellent' && ' üéâ'}
                            {profitLevel === 'good' && ' üü¢'}
                            {profitLevel === 'acceptable' && ' üü°'}
                            {profitLevel === 'poor' && ' üü†'}
                            {profitLevel === 'loss' && ' üî¥'}
                        </span>
                    </div>
                    <Progress
                        value={Math.min(Math.max(plData.profit_margin_percentage, 0), 100)}
                        className="h-2"
                    />
                </div>

                {profitLevel === 'loss' && (
                    <Alert className="border-red-500/50 bg-red-500/10">
                        <AlertTriangle className="h-4 w-4 text-red-500" />
                        <AlertDescription className="text-red-700 dark:text-red-400">
                            <strong>Loss Warning:</strong> Current costs result in a negative margin
                        </AlertDescription>
                    </Alert>
                )}
            </div>

            {/* Payment Status */}
            <div className="mt-6 pt-4 border-t space-y-3">
                <h4 className="text-sm font-semibold">Payment Status</h4>
                <div className="flex justify-between items-center text-sm">
                    <span className="text-muted-foreground">Total Paid:</span>
                    <span className="font-medium text-green-600 dark:text-green-400">
                        {formatCurrency(plData.total_paid_inr)}
                    </span>
                </div>
                <div className="flex justify-between items-center text-sm">
                    <span className="text-muted-foreground">Pending Payments:</span>
                    <span className="font-medium text-amber-600 dark:text-amber-400">
                        {formatCurrency(plData.total_pending_inr)}
                    </span>
                </div>
                {plData.total_payments_inr > 0 && (
                    <div>
                        <div className="flex justify-between items-center mb-2 text-xs text-muted-foreground">
                            <span>Payment Progress</span>
                            <span>
                                {formatPercentage((plData.total_paid_inr / plData.total_payments_inr) * 100)}
                            </span>
                        </div>
                        <Progress
                            value={(plData.total_paid_inr / plData.total_payments_inr) * 100}
                            className="h-2"
                        />
                    </div>
                )}
            </div>
        </Card>
    )
}

'use client'

import { useState } from 'react'
import { useForm } from 'react-hook-form'
import { zodResolver } from '@hookform/resolvers/zod'
import * as z from 'zod'
import { Button } from '@/components/ui/button'
import { Input } from '@/components/ui/input'
import { Textarea } from '@/components/ui/textarea'
import { Label } from '@/components/ui/label'
import {
    Select,
    SelectContent,
    SelectItem,
    SelectTrigger,
    SelectValue,
} from '@/components/ui/select'
import { useToast } from '@/hooks/use-toast'
import { submitInquiry } from '@/app/actions/inquiries'
import { Loader2, CheckCircle2 } from 'lucide-react'

const inquirySchema = z.object({
    fullName: z.string().min(2, 'Name is required'),
    email: z.string().email('Invalid email address'),
    phone: z.string().optional(),
    companyName: z.string().optional(),
    inquiryType: z.enum(['Quote', 'General', 'Partnership']),
    message: z.string().min(10, 'Message must be at least 10 characters')
})

type InquiryFormValues = z.infer<typeof inquirySchema>

export function ContactForm() {
    const [isSubmitting, setIsSubmitting] = useState(false)
    const [isSuccess, setIsSuccess] = useState(false)
    const { toast } = useToast()

    const form = useForm<InquiryFormValues>({
        resolver: zodResolver(inquirySchema),
        defaultValues: {
            inquiryType: 'General',
            message: ''
        }
    })

    const onSubmit = async (data: InquiryFormValues) => {
        setIsSubmitting(true)
        try {
            const result = await submitInquiry(data)

            if (!result.success) {
                throw new Error(result.error)
            }

            setIsSuccess(true)
            toast({
                title: "Inquiry Sent!",
                description: "We've received your message and will respond shortly.",
                variant: 'default'
            })
            form.reset()
        } catch (error: any) {
            toast({
                title: "Submission Failed",
                description: error.message || "Something went wrong. Please try again.",
                variant: "destructive"
            })
        } finally {
            setIsSubmitting(false)
        }
    }

    if (isSuccess) {
        return (
            <div className="bg-emerald-50 dark:bg-emerald-900/20 border border-emerald-200 dark:border-emerald-800 rounded-xl p-8 text-center animate-in fade-in zoom-in duration-300">
                <div className="mx-auto w-16 h-16 bg-emerald-100 dark:bg-emerald-800 rounded-full flex items-center justify-center mb-4">
                    <CheckCircle2 className="h-8 w-8 text-emerald-600 dark:text-emerald-400" />
                </div>
                <h3 className="text-2xl font-bold text-emerald-900 dark:text-emerald-100 mb-2">Message Sent!</h3>
                <p className="text-emerald-700 dark:text-emerald-300 mb-6">
                    Thank you for contacting GVG Global. Our team will review your inquiry and get back to you within 24 hours.
                </p>
                <Button
                    variant="outline"
                    onClick={() => setIsSuccess(false)}
                    className="border-emerald-600 text-emerald-700 hover:bg-emerald-100 dark:hover:bg-emerald-800"
                >
                    Send Another Message
                </Button>
            </div>
        )
    }

    return (
        <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-6 bg-white dark:bg-slate-900 p-8 rounded-xl shadow-sm border border-slate-200 dark:border-slate-800">
            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                <div className="space-y-2">
                    <Label htmlFor="fullName">Full Name *</Label>
                    <Input id="fullName" {...form.register('fullName')} placeholder="John Doe" />
                    {form.formState.errors.fullName && (
                        <p className="text-xs text-red-500">{form.formState.errors.fullName.message}</p>
                    )}
                </div>

                <div className="space-y-2">
                    <Label htmlFor="email">Email Address *</Label>
                    <Input id="email" type="email" {...form.register('email')} placeholder="john@company.com" />
                    {form.formState.errors.email && (
                        <p className="text-xs text-red-500">{form.formState.errors.email.message}</p>
                    )}
                </div>

                <div className="space-y-2">
                    <Label htmlFor="phone">Phone Number</Label>
                    <Input id="phone" {...form.register('phone')} placeholder="+1 (555) 000-0000" />
                </div>

                <div className="space-y-2">
                    <Label htmlFor="companyName">Company Name</Label>
                    <Input id="companyName" {...form.register('companyName')} placeholder="Acme Trading Ltd." />
                </div>
            </div>

            <div className="space-y-2">
                <Label htmlFor="inquiryType">Subject / Type *</Label>
                <Select
                    onValueChange={(val: any) => form.setValue('inquiryType', val)}
                    defaultValue="General"
                >
                    <SelectTrigger>
                        <SelectValue placeholder="Select Type" />
                    </SelectTrigger>
                    <SelectContent>
                        <SelectItem value="General">General Inquiry</SelectItem>
                        <SelectItem value="Quote">Request for Quote (Buy/Sell)</SelectItem>
                        <SelectItem value="Partnership">Partnership Proposal</SelectItem>
                    </SelectContent>
                </Select>
            </div>

            <div className="space-y-2">
                <Label htmlFor="message">Message *</Label>
                <Textarea
                    id="message"
                    {...form.register('message')}
                    placeholder="Tell us about your requirements (e.g., Commodity, Quantity, Port)..."
                    className="min-h-[120px]"
                />
                {form.formState.errors.message && (
                    <p className="text-xs text-red-500">{form.formState.errors.message.message}</p>
                )}
            </div>

            <Button type="submit" className="w-full bg-blue-600 hover:bg-blue-700 text-white" disabled={isSubmitting}>
                {isSubmitting ? (
                    <>
                        <Loader2 className="mr-2 h-4 w-4 animate-spin" />
                        Sending...
                    </>
                ) : (
                    'Send Message'
                )}
            </Button>
        </form>
    )
}

