import { differenceInDays, addDays, format, isPast, isFuture } from 'date-fns';

export type StatusInput = {
    expiryDate: Date; // Current permit expiry
    hasSubmittedExtension: boolean; // Did they apply before expiry?
    currentLocation: 'InsideCanada' | 'OutsideCanada';
};

export type StatusStrategyResult = {
    statusState: 'Valid' | 'Maintained' | 'RestorationPeriod' | 'OutOfStatus';
    daysRemaining: number;
    actionPlan: string[];
    legalNuances: string[]; // "Loopholes" / Tips
    urgentActionRequired: boolean;
};

export function analyzeStatusStrategy(input: StatusInput): StatusStrategyResult {
    const today = new Date();
    const expiry = new Date(input.expiryDate);
    const diff = differenceInDays(expiry, today); // +ve = Future, -ve = Past

    let result: StatusStrategyResult = {
        statusState: 'Valid',
        daysRemaining: diff,
        actionPlan: [],
        legalNuances: [],
        urgentActionRequired: false
    };

    // 1. FUTURE EXPIRY (Valid)
    if (diff >= 0) {
        result.statusState = input.hasSubmittedExtension ? 'Maintained' : 'Valid';

        if (input.hasSubmittedExtension) {
            result.actionPlan.push("You are on Maintained Status (formerly Implied Status).");
            result.actionPlan.push("You can continue working under the exact same conditions as your original permit.");
            result.legalNuances.push("Do not leave Canada. If you leave, you lose Maintained Status and may not be able to work upon re-entry.");
        } else {
            // Options before expiry
            if (diff < 30) {
                result.urgentActionRequired = true;
                result.actionPlan.push("URGENT: Apply for extension IMMEDIATELY.");
                result.actionPlan.push("IRCC recommends 30 days buffer, but technically you can apply up to the last minute (UTC time). Don't risk it.");
            } else if (diff < 90) {
                result.actionPlan.push("Prepare your extension application now.");
            }

            // Strategy: Bridge to Visitor
            result.legalNuances.push("Strategy: If you don't have an ITA or LMIA yet, apply for a Visitor Record BEFORE expiry to stay legal.");
            result.legalNuances.push("Note: Changing to Visitor stops your ability to work, but keeps you in Canada legally.");
        }
    }
    // 2. PAST EXPIRY (Expired)
    else {
        const daysPast = Math.abs(diff);

        if (input.hasSubmittedExtension) {
            result.statusState = 'Maintained';
            result.daysRemaining = 0; // Indefinite until decision
            result.actionPlan.push("You are legally in Canada on Maintained Status.");
            result.legalNuances.push("Keep proof of your application (AOR) on you at all times.");
        } else {
            // NO Extension submitted. 
            if (daysPast <= 90) {
                result.statusState = 'RestorationPeriod';
                result.urgentActionRequired = true;
                result.daysRemaining = 90 - daysPast;

                result.actionPlan.push(`CRITICAL: You are OUT OF STATUS. You have ${90 - daysPast} days left to apply for Restoration.`);
                result.actionPlan.push("STOP WORKING IMMEDIATELY. You have no authorization to work.");
                result.actionPlan.push("Apply for 'Restoration of Status' + New Permit (Worker/Visitor).");
                result.legalNuances.push("Restoration is not guaranteed. You must explain why you overstayed.");
            } else {
                result.statusState = 'OutOfStatus';
                result.urgentActionRequired = true;
                result.daysRemaining = 0;

                result.actionPlan.push("You are beyond the 90-day restoration period.");
                result.actionPlan.push("You have no legal standing to apply for restoration from inside Canada.");
                result.actionPlan.push("Must leave Canada immediately or face deportation/bans.");
                result.legalNuances.push("Consult a lawyer for TRP (Temporary Resident Permit) only in extreme humanitarian cases.");
            }
        }
    }

    return result;
}

export type ExperienceValidationInput = {
    hoursPerWeek: number;
    weeksWorked: number;
    isSeasonal: boolean;
    isStudent: boolean;
    isSelfEmployed: boolean;
    isCoop: boolean;
    programTarget: 'CEC' | 'FSW' | 'FST';
};

export type ExperienceValidationResult = {
    eligibleHours: number;
    eligibleYears: number;
    isContinuous: boolean; // Relevant for FSW
    isValidForProgram: boolean;
    warnings: string[];
    breakdown: string;
};

export function validateWorkExperience(input: ExperienceValidationInput): ExperienceValidationResult {
    const { hoursPerWeek, weeksWorked, isSeasonal, isStudent, isSelfEmployed, isCoop, programTarget } = input;
    let warnings: string[] = [];
    let isValid = true;

    // 1. Cap Hours per Week (Max 30 counted)
    // "You can work more than 30 hours... but the maximum you can count is 30 hours/week"
    const effectiveHoursPerWeek = Math.min(hoursPerWeek, 30);
    const totalHours = effectiveHoursPerWeek * weeksWorked;

    // 2. Minimum Hours for 1 Year
    // 1560 hours is the magic number
    const years = totalHours / 1560;

    let eligibleYears = parseFloat(years.toFixed(2));
    // Cap at actual duration? No, 30hrs/week for 52 weeks = 1.0. 
    // If they worked 15hrs for 104 weeks = 1.0. 

    // 3. Program Specific Exclusions

    // CEC Exclusions
    if (programTarget === 'CEC') {
        if (isStudent) {
            isValid = false;
            warnings.push("Work experience gained while a full-time student does not count for CEC.");
        }
        if (isCoop) {
            isValid = false;
            warnings.push("Co-op work experience does not count for CEC.");
        }
        if (isSelfEmployed) {
            isValid = false;
            warnings.push("Self-employment generally does not count for CEC (unless medical/specific exceptions).");
        }
    }

    // FSW Exclusions
    if (programTarget === 'FSW') {
        // Continuous Check
        // Seasonal work often has gaps. If seasonal, warn about continuity.
        if (isSeasonal) {
            warnings.push("Seasonal work may break 'Continuous Employment' requirement (no gaps allowed).");
        }
        // Student work CAN count for FSW if continuous, paid, and meets description.
        // But usually hard to prove continuity with study.
        if (isStudent) {
            warnings.push("Student work counts for FSW only if continuous and paid. Ensure no gaps.");
        }
    }

    // Work Permit / General
    if (isSeasonal && input.programTarget === 'CEC') {
        // Seasonal allowed for CEC if total hours met? Yes, gaps allowed.
        // But hard to accumulate 1560 hrs.
    }

    // 4. Overtime Warning
    if (hoursPerWeek > 30) {
        warnings.push(`You worked ${hoursPerWeek} hrs/week, but IRCC only counts 30 hrs/week.`);
    }

    return {
        eligibleHours: totalHours,
        eligibleYears: isValid ? eligibleYears : 0,
        isContinuous: !isSeasonal, // Simplification
        isValidForProgram: isValid,
        warnings,
        breakdown: `Counted ${effectiveHoursPerWeek} hrs/week for ${weeksWorked} weeks. Total: ${totalHours} eligible hours.`
    };
}

// Types
export type UserProfile = {
    age: number;
    education: 'High School' | 'Diploma' | 'Bachelors' | 'Masters' | 'PhD';
    clbEnglish: number;
    clbFrench: number;
    workExpCanada: number; // years
    workExpForeign: number; // years
    sector: string;
    jobOfferDest: string | 'None';
    connections: string[]; // Provinces with family
    connectionsFriend: string[]; // Provinces with friends (Important for Manitoba)
}

export type EligibilityResult = {
    program: string;
    eligible: boolean;
    score?: number;
    passScore?: number;
    reason: string;
    probability: 'High' | 'Medium' | 'Low' | 'None';
    checklistId?: string; // Link to specific document list
}

// --- HELPER SCORING FUNCTIONS ---

const getFSWAgePoints = (age: number) => {
    if (age >= 18 && age <= 35) return 12 // Max
    return Math.max(0, 12 - (age - 35))
}

const getEducationPoints = (edu: string, system: 'Federal' | 'MPNP' | 'SINP' | 'BC') => {
    // Simplified mapping
    switch (system) {
        case 'Federal': // Max 25
            if (edu === 'PhD') return 25
            if (edu === 'Masters') return 23
            if (edu === 'Bachelors') return 21
            if (edu === 'Diploma') return 19
            return 5
        case 'MPNP': // Max 25
            if (edu === 'PhD' || edu === 'Masters') return 23 // Master 23, PhD 23? Checked: Masters 23.
            if (edu === 'Bachelors') return 20
            if (edu === 'Diploma') return 19 // 2 years
            return 14
        case 'SINP': // Max 23
            if (edu === 'Masters' || edu === 'PhD') return 23
            if (edu === 'Bachelors') return 20
            if (edu === 'Diploma') return 15
            return 12
        case 'BC': // SIRS Max 40
            if (edu === 'Masters' || edu === 'PhD') return 27 // PhD 27, Masters 22
            if (edu === 'Bachelors') return 15
            if (edu === 'Diploma') return 15
            return 5
    }
    return 0
}

// --- MAIN ENGINE ---
export function calculateEligibility(profile: UserProfile): EligibilityResult[] {
    const results: EligibilityResult[] = [];

    // --- 1. FEDERAL SKILLED WORKER (FSW) - 67 POINTS GRID ---
    let fsw = 0
    // Factor 1: Age (Max 12)
    fsw += getFSWAgePoints(profile.age)
    // Factor 2: Education (Max 25)
    fsw += getEducationPoints(profile.education, 'Federal')
    // Factor 3: Work Experience (Max 15)
    fsw += profile.workExpForeign >= 6 ? 15 : profile.workExpForeign >= 4 ? 13 : profile.workExpForeign >= 2 ? 11 : profile.workExpForeign === 1 ? 9 : 0
    // Factor 4: Language (Max 28 - First Official)
    // Simplified: CLB 9=6/band -> 24. 
    const clbPts = profile.clbEnglish >= 9 ? 24 : profile.clbEnglish >= 8 ? 20 : profile.clbEnglish >= 7 ? 16 : 0
    fsw += clbPts
    // Factor 5: Arranged Employment (Max 10)
    if (profile.jobOfferDest !== 'None') fsw += 10
    // Factor 6: Adaptability (Max 10)
    // Connections, Canadian Exp, etc.
    let adapt = 0
    if (profile.connections.length > 0) adapt += 5
    if (profile.workExpCanada >= 1) adapt += 10
    fsw += Math.min(10, adapt) // Cap at 10

    const isFSW = fsw >= 67

    // CRS Estimator (Rough)
    let crs = 0;
    if (profile.age >= 20 && profile.age <= 29) crs += 110; else crs += 50;
    if (profile.education === 'Masters') crs += 135; else if (profile.education === 'Bachelors') crs += 120;
    if (profile.clbEnglish >= 9) crs += 136; else if (profile.clbEnglish >= 7) crs += 80;
    if (profile.clbFrench >= 7) crs += 50;
    if (profile.workExpCanada >= 1) crs += 40;
    if (profile.workExpForeign >= 3) crs += 50;

    results.push({
        program: 'Federal Skilled Worker (FSW)',
        eligible: isFSW,
        score: fsw,
        passScore: 67,
        reason: isFSW ? `Eligible. Scored ${fsw}/100 points. (Pass: 67). Next step: CRS check.` : `Ineligible. Scored ${fsw}/100. Need 67. Improve Language or Experience.`,
        probability: isFSW ? (crs > 480 ? 'High' : crs > 450 ? 'Medium' : 'Low') : 'None',
        checklistId: 'express-entry'
    });

    results.push({
        program: 'Federal Express Entry (CRS Status)',
        eligible: isFSW || (profile.workExpCanada >= 1 && profile.clbEnglish >= 5), // CEC Logic
        score: crs,
        reason: `Estimated CRS: ${crs}. Recent cut-offs: ~480-525.`,
        probability: crs > 500 ? 'High' : crs > 470 ? 'Medium' : 'Low',
        checklistId: 'express-entry'
    })

    // --- 2. ONTARIO (OINP) ---
    // HCP: CRS Based. Tech Draw: < 460 sometimes.
    const oinpTech = isFSW && ['Tech / IT', 'Healthcare'].includes(profile.sector) && crs > 400;
    results.push({
        program: 'Ontario (OINP) - Human Capital',
        eligible: oinpTech,
        reason: oinpTech ? `Target Sector matches. CRS ${crs} is competitive for Tech/Health draws.` : `Need CRS > 400 & Priority Sector.`,
        probability: oinpTech ? (crs > 460 ? 'High' : 'Medium') : 'Low',
        checklistId: 'oinp-hcp'
    });

    // --- 3. MANITOBA (MPNP) - 100 POINTS GRID ---
    // Connection is MANDATORY for Overseas
    let mpnp = 0
    // Factor 1: Language (Max 25)
    mpnp += profile.clbEnglish >= 8 ? 20 : profile.clbEnglish >= 7 ? 18 : 16
    // Factor 2: Age (Max 10) - 21-45 full pts
    mpnp += (profile.age >= 21 && profile.age <= 45) ? 10 : 0
    // Factor 3: Exp (Max 15) - 4+ years = 15
    mpnp += profile.workExpForeign >= 4 ? 15 : profile.workExpForeign === 3 ? 13 : 11
    // Factor 4: Edu (Max 25)
    mpnp += getEducationPoints(profile.education, 'MPNP')
    // Factor 5: Adaptability (Max 25) - HUGE
    let mpnpConnection = false
    if (profile.connections.includes('Manitoba')) { mpnp += 20; mpnpConnection = true } // Family
    else if (profile.connectionsFriend.includes('Manitoba')) { mpnp += 10; mpnpConnection = true } // Friend (Actually 10 pts for Friend, 20 for Relative)
    // Note: Connection is typically REQUIRED for SWM (Skilled Worker Overseas) unless invited by Strategic Initiative.

    results.push({
        program: 'Manitoba (MPNP)',
        eligible: mpnp >= 60 && mpnpConnection,
        score: mpnp,
        passScore: 60,
        reason: mpnpConnection ? `Scored ${mpnp}/100. Connection verified.` : `Scored ${mpnp}/100 but Ineligible due to missing MB Connection.`,
        probability: (mpnp >= 75 && mpnpConnection) ? 'High' : 'Low',
        checklistId: 'mpnp'
    })

    // --- 4. SASKATCHEWAN (SINP) - 100 POINTS GRID ---
    let sinp = 0
    // Edu (Max 23)
    sinp += getEducationPoints(profile.education, 'SINP')
    // Exp (Max 15) - 5 yrs = 10 (Wait, grid says 10 for 5yrs in last 5. 15 max total maybe?) Simplified to standard.
    sinp += profile.workExpForeign >= 5 ? 10 : 8
    // Language (Max 30) - CLB 8=20
    sinp += profile.clbEnglish >= 8 ? 20 : 18
    // Age (Max 12) - 22-34 full
    sinp += (profile.age >= 22 && profile.age <= 34) ? 12 : 10
    // Connection (Max 30) - Family = 20
    if (profile.connections.includes('Saskatchewan')) sinp += 20

    results.push({
        program: 'Saskatchewan (SINP - OID/EE)',
        eligible: sinp >= 60,
        score: sinp,
        passScore: 60,
        reason: sinp >= 60 ? `Scored ${sinp}/100. Eligible for EOI pool.` : `Scored ${sinp}/100. Need 60.`,
        probability: sinp >= 70 ? 'High' : sinp >= 63 ? 'Medium' : 'Low',
        checklistId: 'sinp'
    })

    // --- 5. BC PNP (SIRS) ---
    // Skills Immigration Registration System
    // Economic Factors (Wage particularly) are hard to guess.
    // We assume median wage for Tech ($80k => ~15-20 pts)
    let sirs = 0
    // Human Capital (Max 120)
    // Exp (Max 40) - Directly related. 5 yrs = 20 pts base.
    sirs += profile.workExpForeign >= 5 ? 20 : 16
    // Edu (Max 40)
    sirs += getEducationPoints(profile.education, 'BC')
    // Lang (Max 40)
    sirs += profile.clbEnglish >= 9 ? 30 : profile.clbEnglish >= 7 ? 20 : 10

    // Economic (Max 80)
    // Wage: Assuming sector avg. Tech/IT high ($40/hr -> ~25 pts). General Low (~10 pts).
    if (profile.sector === 'Tech / IT') sirs += 25
    else if (profile.sector === 'Healthcare') sirs += 20
    else sirs += 10
    // Area: Regional District. Assuming Vancouver (0 pts) for conservative calc.

    const bcPass = 90 // Float variable
    results.push({
        program: 'BC PNP (Skills Immigration)',
        eligible: sirs >= 80, // Loose eligibility
        score: sirs,
        reason: `Est. SIRS Score: ${sirs} (Conservative, assuming Vancouver). Recent Tech draws ~90-100.`,
        probability: sirs >= 100 ? 'High' : sirs >= 90 ? 'Medium' : 'Low',
        checklistId: 'bc-pnp'
    })

    // --- 6. ALBERTA, ATLANTIC, NS, QUEBEC ---
    // (Retain previous logic as they rely more on Job Offer / specific streams than rigid grids manageable here)
    // QUEBEC
    // French is vital.
    let qc = 0
    if (profile.education === 'Masters') qc += 14;
    if (profile.age <= 35) qc += 16;
    if (profile.clbFrench >= 7) qc += 16;
    const qcEligible = qc >= 45 // Very rough "selection" threshold
    results.push({
        program: 'Quebec (Arrima)',
        eligible: qcEligible,
        score: qc,
        reason: `Emphasis on French/Age. Scored ${qc}.`,
        probability: (qc >= 55 && profile.clbFrench >= 7) ? 'High' : 'Low',
        checklistId: 'quebec-arrima'
    })

    return results.sort((a, b) => (a.probability === 'High' ? -1 : 1));
}

// Language Test to CLB Conversion Utilities

export type TestType = 'IELTS' | 'CELPIP' | 'PTE'

// IELTS to CLB Conversion
export function ieltsToCLB(score: number, skill: 'speaking' | 'reading' | 'writing' | 'listening'): number {
    // Listening
    if (skill === 'listening') {
        if (score >= 8.5) return 10
        if (score >= 8.0) return 9
        if (score >= 7.5) return 9
        if (score >= 6.0) return 8
        if (score >= 5.5) return 7
        if (score >= 5.0) return 6
        if (score >= 4.5) return 5
        return 4
    }

    // Reading
    if (skill === 'reading') {
        if (score >= 8.0) return 10
        if (score >= 7.0) return 9
        if (score >= 6.5) return 8
        if (score >= 6.0) return 7
        if (score >= 5.0) return 6
        if (score >= 4.0) return 5
        if (score >= 3.5) return 4
        return 4
    }

    // Writing
    if (skill === 'writing') {
        if (score >= 7.5) return 10
        if (score >= 7.0) return 9
        if (score >= 6.5) return 8
        if (score >= 6.0) return 7
        if (score >= 5.5) return 6
        if (score >= 5.0) return 5
        if (score >= 4.0) return 4
        return 4
    }

    // Speaking
    if (skill === 'speaking') {
        if (score >= 7.5) return 10
        if (score >= 7.0) return 9
        if (score >= 6.5) return 8
        if (score >= 6.0) return 7
        if (score >= 5.5) return 6
        if (score >= 5.0) return 5
        if (score >= 4.0) return 4
        return 4
    }

    return 4
}

// CELPIP (scores are already CLB-aligned, 1-12 scale)
export function celpipToCLB(score: number): number {
    // CELPIP scores directly correspond to CLB levels
    return Math.min(Math.max(score, 4), 12)
}

// PTE to CLB Conversion (approximate mapping)
export function pteToCLB(score: number, skill: 'speaking' | 'reading' | 'writing' | 'listening'): number {
    // Speaking
    if (skill === 'speaking') {
        if (score >= 88) return 10
        if (score >= 84) return 9
        if (score >= 76) return 8
        if (score >= 68) return 7
        if (score >= 59) return 6
        if (score >= 51) return 5
        if (score >= 42) return 4
        return 4
    }

    // Listening
    if (skill === 'listening') {
        if (score >= 88) return 10
        if (score >= 82) return 9
        if (score >= 71) return 8
        if (score >= 60) return 7
        if (score >= 50) return 6
        if (score >= 39) return 5
        if (score >= 28) return 4
        return 4
    }

    // Reading
    if (skill === 'reading') {
        if (score >= 88) return 10
        if (score >= 78) return 9
        if (score >= 69) return 8
        if (score >= 60) return 7
        if (score >= 51) return 6
        if (score >= 42) return 5
        if (score >= 33) return 4
        return 4
    }

    // Writing
    if (skill === 'writing') {
        if (score >= 88) return 10
        if (score >= 79) return 9
        if (score >= 69) return 8
        if (score >= 60) return 7
        if (score >= 51) return 6
        if (score >= 41) return 5
        if (score >= 32) return 4
        return 4
    }

    return 4
}

// Generate score options for dropdown
export function getScoreOptions(testType: TestType, skill: string): { value: string; label: string; clb: number }[] {
    if (testType === 'CELPIP') {
        return Array.from({ length: 9 }, (_, i) => {
            const score = i + 4
            return { value: score.toString(), label: `${score}`, clb: score }
        })
    }

    if (testType === 'IELTS') {
        const scores = [4.0, 4.5, 5.0, 5.5, 6.0, 6.5, 7.0, 7.5, 8.0, 8.5, 9.0]
        return scores.map(score => ({
            value: score.toString(),
            label: score.toFixed(1),
            clb: ieltsToCLB(score, skill as any)
        }))
    }

    if (testType === 'PTE') {
        const scores = Array.from({ length: 59 }, (_, i) => i + 30) // 30-90
        return scores.filter(s => s % 5 === 0 || [42, 51, 59, 68, 76, 84, 88].includes(s)).map(score => ({
            value: score.toString(),
            label: score.toString(),
            clb: pteToCLB(score, skill as any)
        }))
    }

    return []
}

