export type LegalProfile = {
    hasCriminalRecord: boolean;
    convictionDate?: Date; // For Deemed Rehab
    sentenceCompletedDate?: Date;
    offenseType?: 'DUI' | 'Theft' | 'Assault' | 'Fraud' | 'Other';
    offenseMaxSentenceInCanada?: number; // 10 years = Serious Criminality

    hasMedicalCondition: boolean;
    conditionType?: 'Chronic' | 'Developmental' | 'MentalHealth' | 'Other';
    annualTreatmentCost?: number; // Threshold ~$27,000

    hasRefusalHistory: boolean;
    refusalReason?: 'Misrepresentation' | 'Funds' | 'ReferenceLetter' | 'Other';

    isFlagpolingConsidered: boolean;
}

export type DefenseResult = {
    inadmissibility: {
        isInadmissible: boolean;
        ground: string; // 'Serious Criminality', 'Medical Excessive Demand'
        severity: 'Critical' | 'High' | 'Medium';
    };
    defenseStrategy: {
        title: string;
        arguments: string[]; // "Argue Deemed Rehabilitation via s.36(2)"
        recommendedDocuments: string[]; // "Legal Opinion Letter", "Medical Cost Projection Specialist Report"
    }[];
    probabilityOfSuccess: 'Low' | 'Medium' | 'High';
    lawyerNote: string;
}

export function analyzeLegalDefense(p: LegalProfile): DefenseResult {
    let result: DefenseResult = {
        inadmissibility: { isInadmissible: false, ground: '', severity: 'Medium' },
        defenseStrategy: [],
        probabilityOfSuccess: 'High',
        lawyerNote: "Standard Application Risk"
    };

    const YEAR_MS = 365 * 24 * 60 * 60 * 1000;
    const now = new Date();

    // 1. CRIMINALITY CHECK (S.36 IRPA)
    if (p.hasCriminalRecord && p.sentenceCompletedDate) {
        const timeSinceSentence = (now.getTime() - p.sentenceCompletedDate.getTime()) / YEAR_MS;
        const isSerious = (p.offenseMaxSentenceInCanada || 0) >= 10; // e.g., Fraud > $5k, DUI (post-2018)

        if (isSerious) {
            result.inadmissibility = { isInadmissible: true, ground: 'Serious Criminality (s.36(1))', severity: 'Critical' };
            result.probabilityOfSuccess = 'Low';
            result.lawyerNote = "Serious Criminality bars 'Deemed Rehabilitation'. Must apply for Criminal Rehabilitation.";

            if (timeSinceSentence >= 5) {
                result.defenseStrategy.push({
                    title: "Application for Criminal Rehabilitation",
                    arguments: ["Demonstrate 5 years of stability since sentence completion", "Prove low risk to Canadian society"],
                    recommendedDocuments: ["Form IMM 1444", "Police Certificates (All countries)", "Reference Letters regarding character"]
                });
            } else {
                result.defenseStrategy.push({
                    title: "Temporary Resident Permit (TRP) - Hardship Argument",
                    arguments: ["Need to enter Canada outweighs risk", "Compelling economic or family reason"],
                    recommendedDocuments: ["TRP Application", "Proof of significant benefit to Canada"]
                });
            }
        } else {
            // Non-Serious (s.36(2))
            if (timeSinceSentence >= 10) {
                result.inadmissibility = { isInadmissible: false, ground: 'Deemed Rehabilitated', severity: 'Medium' };
                result.defenseStrategy.push({
                    title: "Deemed Rehabilitation Defense",
                    arguments: ["10 years passed since sentence completion", "No subsequent offenses"],
                    recommendedDocuments: ["Legal Opinion Letter citing s.18(2) IRPR regulations"]
                });
            } else if (timeSinceSentence >= 5) {
                result.inadmissibility = { isInadmissible: true, ground: 'Criminality (Rehab eligible)', severity: 'Medium' };
                result.defenseStrategy.push({
                    title: "Apply for Rehabilitation",
                    arguments: ["5 years passed", "Good moral character"],
                    recommendedDocuments: ["Form IMM 1444"]
                });
            }
        }
    }

    // 2. MEDICAL INADMISSIBILITY (S.38 IRPA)
    if (p.hasMedicalCondition) {
        const THRESHOLD_2025 = 27162; // CAD/yr
        // Exemptions: Family Spouse/Child, Refugees
        // Assuming Economic Class for this engine (Non-exempt)

        if ((p.annualTreatmentCost || 0) > THRESHOLD_2025) {
            result.inadmissibility = { isInadmissible: true, ground: 'Excessive Demand on Health Services', severity: 'High' };
            result.probabilityOfSuccess = 'Medium';
            result.defenseStrategy.push({
                title: "Procedural Fairness Response - Mitigation Plan",
                arguments: [
                    "Ability to defray costs (private insurance not always accepted but willingness helps)",
                    "Challenge the officer's cost calculation (Fairness Letter)",
                    "Provide specialist report proving lower actual costs than generic average"
                ],
                recommendedDocuments: ["Specialist Medical Report", "Individualized Cost Mitigation Plan"]
            })
        }
    }

    // 3. PRIOR REFUSALS & MISREP (S.40 IRPA)
    if (p.hasRefusalHistory) {
        if (p.refusalReason === 'Misrepresentation') {
            result.inadmissibility = { isInadmissible: true, ground: 'Misrepresentation (5-Year Ban)', severity: 'Critical' };
            result.probabilityOfSuccess = 'Low';
            result.defenseStrategy.push({
                title: "Challenge Finding of Materiality",
                arguments: ["Error was innocent mistake, not fraudulent intent", "Information was not material to the decision"],
                recommendedDocuments: ["Affidavit explaining error", "Proof of attempt to correct"]
            });
        }
        if (p.refusalReason === 'ReferenceLetter') {
            result.defenseStrategy.push({
                title: "Strengthened Reference Letter Strategy",
                arguments: ["Provide notarized colleague affidavits if HR refuses specific duties", "Include pay stubs/T4s to prove employment reality"],
                recommendedDocuments: ["Notarized Supervisor Affidavit", "Pay Stubs", "Explanation Letter"]
            });
        }
    }

    // 4. FLAGPOLING (Procedural)
    if (p.isFlagpolingConsidered) {
        result.defenseStrategy.push({
            title: "Flagpoling Risk Mitigation",
            arguments: ["Ensure strict eligibility before arriving at POE", "POE officers have high discretion - verify quiet hours"],
            recommendedDocuments: ["Complete Application Package printed", "Proof of Status in US (if applicable)"]
        })
    }

    return result;
}

import { CandidateProfile, ProgramResult } from './types';

// ALBERTA (AAIP) & ATLANTIC (AIP)
// These are less grid-heavy and more threshold-based.

export function calculateAAIP(p: CandidateProfile, crs: number): ProgramResult[] {
    const results: ProgramResult[] = [];

    // 1. Alberta Opportunity Stream (AOS) - Paused?
    // Req: Job Offer in AB (Eligible occupation).
    const hasABJob = p.workExperience.jobOfferProvince === 'Alberta';
    if (hasABJob) {
        results.push({
            province: 'Alberta',
            stream: 'Alberta Opportunity Stream (AOS)',
            score: 0,
            maxScore: 0,
            eligible: true,
            drawProbability: 'Medium', // Varies by sector
            warnings: ['Check for current pause/intake status', 'In-demand sectors prioritized (Construction/Health)'],
            checklist: ['ab_job_offer', 'lmia_or_exemption']
        });
    }

    // 2. Alberta Express Entry (Tech Pathway / Health / General)
    // Req: CRS > 300. Tech job offer? Or family connection?
    // Tech: Job offer in Tech NOC.
    const isTech = p.workExperience.jobOfferNoc === 'Tech'; // Proxy
    const hasFamily = p.connections.relativeInProvince.includes('Alberta');

    if (crs >= 300 && p.intentProvinces.includes('Alberta')) {
        let prob: 'High' | 'Medium' | 'Low' | 'None' = 'Low';
        let note = '';

        if (isTech && hasABJob) {
            prob = 'High';
            note = 'Accelerated Tech Pathway';
        } else if (['Construction', 'Health'].includes(p.workExperience.jobOfferNoc)) {
            prob = 'High';
            note = 'Priority Sector';
        } else if (hasFamily) {
            prob = 'Medium';
            note = 'Family Connection Factor';
        }

        results.push({
            province: 'Alberta',
            stream: 'Express Entry Stream',
            score: crs,
            maxScore: 1200,
            eligible: true,
            drawProbability: prob,
            warnings: ['NOI (Notification of Interest) required from Alberta'],
            checklist: ['ee_profile_valid']
        });
    }

    return results;
}

export function calculateAtlantic(p: CandidateProfile): ProgramResult[] {
    const results: ProgramResult[] = [];

    // Atlantic Immigration Program (AIP)
    // Req: Job Offer from Designated Employer in NB, NS, NL, PEI.
    // This is the SINGLE biggest factor.
    const atlanticProvs = ['New Brunswick', 'Nova Scotia', 'Newfoundland', 'PEI'];
    const hasJob = atlanticProvs.includes(p.workExperience.jobOfferProvince);

    results.push({
        province: 'Atlantic Canada (AIP)',
        stream: 'Atlantic Immigration Program',
        score: 0,
        maxScore: 0,
        eligible: hasJob,
        drawProbability: hasJob ? 'High' : 'None',
        warnings: ['Requires Job Offer from Designated Employer'],
        checklist: ['endorsement_certificate', 'designated_employer_offer']
    });

    return results;
}

import { CandidateProfile, ProgramResult } from './types';

// OINP EOI SCORING GRID (Masters / PhD / Employer Job Offer / Foreign Worker)
// Based on 2024/2025 Factors

function calculateOINPEOI(p: CandidateProfile, stream: 'Masters' | 'PhD'): number {
    let score = 0;

    // 1. Job Offer Level (N/A for Masters/PhD usually, but if they have one it helps in other streams)
    // Masters/PhD Stream Factors specifically:

    // Employment / Labour Market Factors
    // (Skipping Job Offer points as it's not core for Grad streams, but "Earnings" is)

    // Earnings History (Annual earnings in last 2 years)
    // >40k = 3 pts. (simplified)
    const earnings = p.workExperience.workHistory.reduce((max, job) => Math.max(max, job.earnings), 0);
    if (earnings >= 40000) score += 3;

    // Education (Highest)
    if (stream === 'PhD' || p.educationLevel === 'PhD') score += 10;
    else if (stream === 'Masters' || p.educationLevel === 'Masters') score += 8;
    else if (p.educationLevel === 'Bachelors') score += 6;

    // Field of Study
    if (p.fieldOfStudy === 'STEM_Health_Trades') score += 12;
    else if (p.fieldOfStudy === 'Business_Admin') score += 6;

    // Canadian Education Credential
    // 2+ credentials = 10? No, checking OINP specific:
    // One credential = 5. Two or more = 10.
    if (p.canadianEducation.completed) {
        // Assuming user input validates "2 or more", for now we give 5 base.
        score += 5;
    }

    // Official Language
    // CLB 9+ = 10
    // CLB 8 = 6
    // CLB 7 = 4
    const clb = Math.min(p.clbEnglish.l, p.clbEnglish.r, p.clbEnglish.w, p.clbEnglish.s);
    if (clb >= 9) score += 10;
    else if (clb >= 8) score += 6;
    else if (clb >= 7) score += 4;

    // Knowledge of 2L (French)
    const fr = Math.min(p.clbFrench.l, p.clbFrench.r, p.clbFrench.w, p.clbFrench.s);
    if (fr >= 6 && clb >= 6) score += 10; // Bilingual
    else if (fr >= 6) score += 5; // French only? (Usually add-on)

    // Regional Immigration (Location of Study)
    // Northern ON = 10
    // Outside GTA = 8
    // GTA (excl Toronto) = 3
    // Toronto = 0
    // We need a specific input for "Location of Study". Assuming profile.canadianEducation.province === 'Ontario'
    // For MVP we assume "Outside GTA" average (8) if they select Ontario study, 
    // but ideally we need a "StudyCity" input. 
    // logic: If studied in Ontario...
    if (p.canadianEducation.province === 'Ontario') {
        // Placeholder: Needs granular city input.
        // We'll give conservative score (3 - GTA) or make generic. 
        // User asked "not single point miss". We can't guess city.
        // For now, we'll auto-score 8 (Outside GTA) as middle ground or 0.
        // TODO: Add `studyLocation` to profile.
        score += 3;
    }

    return score;
}

export function calculateOINP(p: CandidateProfile, crs: number): ProgramResult[] {
    const results: ProgramResult[] = [];

    // 1. Masters Graduate Stream
    // Req: Masters from ON, CLB 7+, Resided 1yr in ON.
    const isMasters = p.educationLevel === 'Masters' &&
        p.canadianEducation.province === 'Ontario' &&
        Math.min(p.clbEnglish.l, p.clbEnglish.s) >= 7 &&
        (p.workExperience.province === 'Ontario' || p.connections.pastStudy.includes('Ontario')); // 1yr residency proxy

    if (isMasters) {
        const score = calculateOINPEOI(p, 'Masters');
        // Cutoff usually ~40-50 range? 
        results.push({
            province: 'Ontario',
            stream: 'Masters Graduate Stream',
            score: score,
            maxScore: 70, // theoretical max roughly
            eligible: true,
            drawProbability: score >= 45 ? 'High' : 'Medium', // Recent draws ~40-50
            warnings: ['Ensure you resided in Ontario for 12 months in last 2 years'],
            checklist: ['oinp_masters_degree', 'residency_proof_on', 'funds_proof']
        });
    }

    // 2. Human Capital Priorities (Express Entry)
    // Req: Bachelor+, CLB 7+, CRS > 400 (usually 460+ unless Tech/Health)
    const isHCP = ['Bachelors', 'Masters', 'PhD'].includes(p.educationLevel) &&
        Math.min(p.clbEnglish.l, p.clbEnglish.s) >= 7 &&
        crs >= 400; // Technical cutoff

    if (isHCP) {
        let prob: 'High' | 'Medium' | 'Low' | 'None' = 'Low';
        let note = '';

        if (['Tech', 'Health'].includes(p.workExperience.jobOfferNoc) && crs >= 460) {
            prob = 'High';
            note = 'Tech/Health Draw likely';
        } else if (crs > 480) {
            prob = 'High';
            note = 'General Draw competitive';
        }

        results.push({
            province: 'Ontario',
            stream: 'Express Entry: Human Capital Priorities',
            score: crs,
            maxScore: 1200,
            eligible: true,
            drawProbability: prob,
            warnings: ['Must have active Express Entry profile'],
            checklist: ['ee_profile_number', 'job_seeker_code']
        });
    }

    // 3. Skilled Trades (Express Entry)
    // Req: Work in ON in trade (NOC 63, 72, 73, 82), CLB 5+, Resided in ON.
    if (['Construction', 'Trades'].includes(p.workExperience.jobOfferNoc) &&
        p.workExperience.canadian >= 1 &&
        p.workExperience.province === 'Ontario') {
        results.push({
            province: 'Ontario',
            stream: 'Express Entry: Skilled Trades',
            score: crs,
            maxScore: 1200,
            eligible: true,
            drawProbability: crs > 350 ? 'High' : 'Medium', // Trades draws are lower CRS
            warnings: [],
            checklist: ['trade_certification_on']
        })
    }

    return results;
}

/**
 * Supabase Storage Configuration and Helper Functions
 * For deal document management
 */

import { createClient } from '@/lib/supabase/client'


export const STORAGE_BUCKET = 'deal-docs-private'

export const DOCUMENT_TAGS = [
    'Bill of Lading',
    'Commercial Invoice',
    'PSIC',
    'Weight Slip',
    'Bill of Entry',
    'Packing List',
    'Certificate of Origin',
    'Insurance Certificate',
    'Quality Certificate',
    'Other'
] as const

export type DocumentTag = typeof DOCUMENT_TAGS[number]

export const ALLOWED_FILE_TYPES = [
    'application/pdf',
    'image/jpeg',
    'image/jpg',
    'image/png',
    'application/vnd.openxmlformats-officedocument.spreadsheetml.sheet', // xlsx
    'application/vnd.ms-excel', // xls
] as const

export const MAX_FILE_SIZE = 10 * 1024 * 1024 // 10MB

export interface UploadDocumentParams {
    dealId: string
    file: File
    documentTag: DocumentTag
    notes?: string
}

export interface DocumentMetadata {
    id: string
    deal_id: string
    file_name: string
    file_path: string
    file_size: number
    file_type: string
    document_tag: DocumentTag
    uploaded_by: string
    uploaded_at: string
    notes?: string
    version: number
    is_current: boolean
}

/**
 * Validate file before upload
 */
export function validateFile(file: File): { valid: boolean; error?: string } {
    // Check file size
    if (file.size > MAX_FILE_SIZE) {
        return {
            valid: false,
            error: `File size must be less than ${MAX_FILE_SIZE / 1024 / 1024}MB`
        }
    }

    // Check file type
    if (!ALLOWED_FILE_TYPES.includes(file.type as any)) {
        return {
            valid: false,
            error: 'Invalid file type. Allowed: PDF, JPG, PNG, Excel'
        }
    }

    return { valid: true }
}

/**
 * Generate storage path for document
 */
export function generateStoragePath(dealId: string, fileName: string): string {
    const timestamp = Date.now()
    const sanitizedName = fileName.replace(/[^a-zA-Z0-9.-]/g, '_')
    return `${dealId}/${timestamp}-${sanitizedName}`
}

/**
 * Upload document to Supabase Storage
 */
export async function uploadDocument(
    params: UploadDocumentParams,
    userId: string
): Promise<{ success: boolean; data?: DocumentMetadata; error?: string }> {
    const { dealId, file, documentTag, notes } = params

    try {
        // Validate file
        const validation = validateFile(file)
        if (!validation.valid) {
            return { success: false, error: validation.error }
        }

        const supabase = createClient()

        // Generate storage path
        const storagePath = generateStoragePath(dealId, file.name)

        // Upload to storage
        const { data: uploadData, error: uploadError } = await supabase.storage
            .from(STORAGE_BUCKET)
            .upload(storagePath, file, {
                cacheControl: '3600',
                upsert: false
            })

        if (uploadError) {
            console.error('Storage upload error:', uploadError)
            return { success: false, error: uploadError.message }
        }

        // Create metadata record
        const { data: metadataData, error: metadataError } = await supabase
            .from('deal_documents')
            .insert({
                deal_id: dealId,
                file_name: file.name,
                file_path: uploadData.path,
                file_size: file.size,
                file_type: file.type,
                document_tag: documentTag,
                uploaded_by: userId,
                notes: notes || null
            })
            .select()
            .single() as any

        if (metadataError) {
            // Rollback: delete uploaded file
            await supabase.storage.from(STORAGE_BUCKET).remove([storagePath])
            console.error('Metadata creation error:', metadataError)
            return { success: false, error: metadataError.message }
        }

        return { success: true, data: metadataData }
    } catch (error) {
        console.error('Upload document error:', error)
        return {
            success: false,
            error: error instanceof Error ? error.message : 'Upload failed'
        }
    }
}

/**
 * Get signed URL for downloading document
 */
export async function getDocumentDownloadUrl(
    filePath: string
): Promise<{ url?: string; error?: string }> {
    try {
        const supabase = createClient()

        const { data, error } = await supabase.storage
            .from(STORAGE_BUCKET)
            .createSignedUrl(filePath, 3600) // 1 hour expiry

        if (error) {
            return { error: error.message }
        }

        return { url: data.signedUrl }
    } catch (error) {
        return {
            error: error instanceof Error ? error.message : 'Failed to generate URL'
        }
    }
}

/**
 * Delete document (admin only)
 */
export async function deleteDocument(
    documentId: string
): Promise<{ success: boolean; error?: string }> {
    try {
        const supabase = createClient()

        // Get document metadata
        const { data: doc, error: fetchError } = await supabase
            .from('deal_documents')
            .select('file_path')
            .eq('id', documentId)
            .single() as any

        if (fetchError || !doc) {
            return { success: false, error: 'Document not found' }
        }

        // Delete from storage
        const { error: storageError } = await supabase.storage
            .from(STORAGE_BUCKET)
            .remove([doc.file_path])

        if (storageError) {
            console.warn('Storage deletion warning:', storageError)
            // Continue anyway to delete metadata
        }

        // Delete metadata
        const { error: deleteError } = await supabase
            .from('deal_documents')
            .delete()
            .eq('id', documentId)

        if (deleteError) {
            return { success: false, error: deleteError.message }
        }

        return { success: true }
    } catch (error) {
        return {
            success: false,
            error: error instanceof Error ? error.message : 'Delete failed'
        }
    }
}

/**
 * Get all documents for a deal
 */
export async function getDealDocuments(
    dealId: string
): Promise<{ documents?: DocumentMetadata[]; error?: string }> {
    try {
        const supabase = createClient()

        const { data, error } = await supabase
            .from('deal_documents')
            .select(`
        *,
        uploader:profiles!uploaded_by(
          full_name,
          email
        )
      `)
            .eq('deal_id', dealId)
            .eq('is_current', true)
            .order('uploaded_at', { ascending: false })

        if (error) {
            return { error: error.message }
        }

        return { documents: data as any }
    } catch (error) {
        return {
            error: error instanceof Error ? error.message : 'Failed to fetch documents'
        }
    }
}

/**
 * Check if deal has required document
 */
export async function hasRequiredDocument(
    dealId: string,
    documentTag: DocumentTag
): Promise<boolean> {
    try {
        const supabase = createClient()

        const { data, error } = await supabase
            .from('deal_documents')
            .select('id')
            .eq('deal_id', dealId)
            .eq('document_tag', documentTag)
            .eq('is_current', true)
            .single()

        return !!data && !error
    } catch {
        return false
    }
}

/**
 * Format file size for display
 */
export function formatFileSize(bytes: number): string {
    if (bytes === 0) return '0 Bytes'
    const k = 1024
    const sizes = ['Bytes', 'KB', 'MB', 'GB']
    const i = Math.floor(Math.log(bytes) / Math.log(k))
    return Math.round((bytes / Math.pow(k, i)) * 100) / 100 + ' ' + sizes[i]
}

